public/css/blog-page.css.content:
/* Blog Page Styles */

.blog-page {
  background: #0B0D10;
  min-height: 100vh;
  color: #FFFFFF;
  position: relative;
  overflow-x: hidden;
}

.blog-page::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 20% 80%, rgba(59, 124, 255, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(242, 106, 48, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(89, 196, 182, 0.03) 0%, transparent 50%);
  pointer-events: none;
  z-index: 0;
}

/* Hero Section */
.blog-hero {
  background: linear-gradient(135deg, #0B0D10 0%, #1A1C20 100%);
  padding: 140px 0 60px;
  position: relative;
  overflow: hidden;
}

.blog-hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(ellipse at center, rgba(59, 124, 255, 0.08) 0%, transparent 70%);
  pointer-events: none;
}

.blog-hero-content {
  text-align: center;
  position: relative;
  z-index: 2;
}

.blog-breadcrumb {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  margin-bottom: 24px;
  font-size: 14px;
  color: #8B9095;
}

.blog-breadcrumb a {
  color: #3B7CFF;
  text-decoration: none;
  transition: color 0.3s ease;
}

.blog-breadcrumb a:hover {
  color: #5A94FF;
}

.blog-breadcrumb i {
  width: 16px;
  height: 16px;
}

.blog-hero-title {
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 24px;
  line-height: 1.2;
  background: linear-gradient(135deg, #FFFFFF 0%, #B0B3B8 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.blog-hero-subtitle {
  font-size: 1.2rem;
  color: #B0B3B8;
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
}

/* Blog Content */
.blog-content {
  padding: 40px 0 120px;
}

.blog-layout {
  display: grid;
  grid-template-columns: 300px 1fr;
  gap: 60px;
}

/* Sidebar */
.blog-sidebar {
  position: sticky;
  top: 120px;
  height: fit-content;
}

.sidebar-widget {
  background: linear-gradient(180deg, #2A3C4E 0%, #1D1F22 100%);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 16px;
  padding: 24px;
  margin-bottom: 32px;
}

.sidebar-widget h3 {
  font-size: 1.1rem;
  font-weight: 600;
  margin-bottom: 20px;
  color: #FFFFFF;
}

.category-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.category-list li {
  margin-bottom: 12px;
}

.category-list a {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 8px 12px;
  color: #B0B3B8;
  text-decoration: none;
  border-radius: 8px;
  transition: all 0.3s ease;
  font-size: 14px;
}

.category-list a:hover {
  background: rgba(255, 255, 255, 0.05);
  color: #FFFFFF;
}

.category-list span {
  font-size: 12px;
  color: #8B9095;
}

/* Popular Posts */
.popular-posts {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.popular-post {
  display: flex;
  gap: 12px;
  padding: 12px;
  border-radius: 8px;
  transition: background 0.3s ease;
}

.popular-post:hover {
  background: rgba(255, 255, 255, 0.03);
}

.post-thumbnail {
  flex-shrink: 0;
}

.post-icon {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #3B7CFF 0%, #5A94FF 100%);
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #FFFFFF;
}

.post-icon i {
  width: 18px;
  height: 18px;
}

.post-content h4 {
  margin: 0 0 4px 0;
  font-size: 14px;
  font-weight: 500;
  line-height: 1.3;
}

.post-content a {
  color: #FFFFFF;
  text-decoration: none;
}

.post-content a:hover {
  color: #3B7CFF;
}

.post-date {
  font-size: 12px;
  color: #8B9095;
}

/* CTA Widget */
.cta-widget {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
  border: none;
  text-align: center;
}

.cta-widget h3 {
  color: #FFFFFF;
  margin-bottom: 12px;
}

.cta-widget p {
  color: rgba(255, 255, 255, 0.9);
  font-size: 14px;
  margin-bottom: 20px;
  line-height: 1.5;
}

.cta-button {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 12px 24px;
  background: #FFFFFF;
  color: #F26A30;
  text-decoration: none;
  border-radius: 8px;
  font-weight: 600;
  font-size: 14px;
  transition: all 0.3s ease;
}

.cta-button:hover {
  background: rgba(255, 255, 255, 0.9);
  transform: translateY(-2px);
}

/* Main Content */
.blog-main {
  flex: 1;
}

.blog-search-filters {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 40px;
  gap: 20px;
}

.search-box {
  position: relative;
  max-width: 350px;
  flex: 1;
}

.search-box input {
  width: 100%;
  padding: 12px 16px 12px 45px;
  background: rgba(255, 255, 255, 0.08);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  color: #FFFFFF;
  font-size: 14px;
  transition: all 0.3s ease;
}

.search-box input:focus {
  background: rgba(255, 255, 255, 0.12);
  border-color: #3B7CFF;
  outline: none;
  box-shadow: 0 0 0 3px rgba(59, 124, 255, 0.2);
}

.search-box input::placeholder {
  color: #8B9095;
}

.search-box i {
  position: absolute;
  left: 16px;
  top: 50%;
  transform: translateY(-50%);
  color: #8B9095;
  width: 18px;
  height: 18px;
}

.filter-tabs {
  display: flex;
  gap: 8px;
}

.filter-tab {
  padding: 10px 20px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  color: #B0B3B8;
  font-size: 13px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.filter-tab:hover {
  background: rgba(255, 255, 255, 0.08);
  border-color: rgba(255, 255, 255, 0.2);
}

.filter-tab.active {
  background: rgba(59, 124, 255, 0.2);
  border-color: #3B7CFF;
  color: #FFFFFF;
}

/* Blog Grid */
.blog-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
  gap: 32px;
  margin-bottom: 60px;
}

/* Blog Articles */
.blog-article {
  background: linear-gradient(145deg, rgba(42, 60, 78, 0.4) 0%, rgba(29, 31, 34, 0.6) 50%, rgba(43, 45, 49, 0.4) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 36px;
  position: relative;
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  display: flex;
  flex-direction: column;
  overflow: hidden;
  box-shadow: 
    0 8px 32px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.blog-article::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, #F26A30, #3B7CFF, #59C4B6);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.blog-article:hover {
  transform: translateY(-12px) scale(1.02);
  background: linear-gradient(145deg, rgba(44, 54, 69, 0.5) 0%, rgba(29, 31, 34, 0.7) 50%, rgba(43, 45, 49, 0.5) 100%);
  border-color: rgba(255, 255, 255, 0.2);
  box-shadow: 
    0 25px 50px rgba(0, 0, 0, 0.5),
    0 12px 24px rgba(59, 124, 255, 0.15),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

.blog-article:hover::before {
  opacity: 1;
}

.article-badge {
  position: absolute;
  top: 20px;
  right: 20px;
  background: #FF6A2D;
  color: #FFFFFF;
  padding: 6px 12px;
  border-radius: 6px;
  font-size: 11px;
  font-weight: 700;
  letter-spacing: 0.5px;
  border: 1px solid #FF934D;
  animation: badgeGlow 2s infinite alternate;
}

.article-header {
  display: flex;
  align-items: flex-start;
  gap: 16px;
  margin-bottom: 20px;
}

.article-icon {
  width: 56px;
  height: 56px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

.article-icon.orange {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
}

.article-icon.blue {
  background: linear-gradient(135deg, #3AAFF4 0%, #3B7CFF 100%);
}

.article-icon.teal {
  background: linear-gradient(135deg, #59C4B6 0%, #61A8A1 100%);
}

.article-icon.purple {
  background: linear-gradient(135deg, #5A6C89 0%, #2C3645 100%);
}

.article-icon i {
  width: 24px;
  height: 24px;
  color: #FFFFFF;
}

.article-meta {
  flex: 1;
}

.category {
  font-size: 11px;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: #3AAFF4;
  margin-bottom: 8px;
  display: block;
}

.meta-info {
  display: flex;
  gap: 16px;
  font-size: 12px;
  color: #8B9095;
}

.meta-info span {
  display: flex;
  align-items: center;
  gap: 4px;
}

.meta-info i {
  width: 14px;
  height: 14px;
}

.blog-article h2 {
  font-size: 1.6rem;
  font-weight: 700;
  color: #FFFFFF;
  line-height: 1.3;
  margin-bottom: 20px;
  background: linear-gradient(135deg, #FFFFFF 0%, #E0E4E8 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.02em;
}

.blog-article p {
  color: #C1C4C8;
  line-height: 1.7;
  margin-bottom: 24px;
  font-size: 15px;
  flex-grow: 1;
  font-weight: 400;
}

.article-tags {
  display: flex;
  gap: 8px;
  margin-bottom: 24px;
  flex-wrap: wrap;
}

.tag {
  padding: 4px 12px;
  background: rgba(255, 255, 255, 0.1);
  color: #B0B3B8;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 500;
}

.read-more-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 12px 24px;
  border-radius: 8px;
  font-weight: 600;
  font-size: 14px;
  text-decoration: none;
  color: #FFFFFF;
  transition: all 0.3s ease;
  margin: 0 auto;
  width: fit-content;
}

.read-more-btn.orange {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
  box-shadow: 0 4px 12px rgba(242, 106, 48, 0.25);
}

.read-more-btn.orange:hover {
  background: linear-gradient(135deg, #FF934D 0%, #FFB366 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(242, 106, 48, 0.4);
}

.read-more-btn.blue {
  background: linear-gradient(135deg, #3B7CFF 0%, #5A94FF 100%);
  box-shadow: 0 4px 12px rgba(59, 124, 255, 0.25);
}

.read-more-btn.blue:hover {
  background: linear-gradient(135deg, #5A94FF 0%, #7AABFF 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(59, 124, 255, 0.4);
}

.read-more-btn i {
  width: 16px;
  height: 16px;
  transition: transform 0.3s ease;
}

.read-more-btn:hover i {
  transform: translateX(3px);
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 16px;
  margin-top: 60px;
}

.page-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 20px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  color: #B0B3B8;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.page-btn:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.08);
  border-color: rgba(255, 255, 255, 0.2);
  color: #FFFFFF;
}

.page-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.page-numbers {
  display: flex;
  gap: 8px;
}

.page-num {
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  color: #B0B3B8;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.page-num:hover {
  background: rgba(255, 255, 255, 0.08);
  border-color: rgba(255, 255, 255, 0.2);
  color: #FFFFFF;
}

.page-num.active {
  background: rgba(59, 124, 255, 0.2);
  border-color: #3B7CFF;
  color: #FFFFFF;
}

/* Responsive */
@media (max-width: 1200px) {
  .blog-layout {
    grid-template-columns: 280px 1fr;
    gap: 40px;
  }

  .blog-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 968px) {
  .blog-layout {
    grid-template-columns: 1fr;
    gap: 40px;
  }

  .blog-sidebar {
    position: static;
    order: 2;
  }

  .blog-main {
    order: 1;
  }

  .blog-search-filters {
    flex-direction: column;
    gap: 16px;
    align-items: stretch;
  }

  .search-box {
    max-width: 100%;
  }

  .filter-tabs {
    justify-content: center;
    flex-wrap: wrap;
  }
}

@media (max-width: 768px) {
  .blog-hero {
    padding: 100px 0 60px;
  }

  .blog-hero-title {
    font-size: 2.5rem;
  }

  .blog-content {
    padding: 60px 0 100px;
  }

  .blog-grid {
    grid-template-columns: 1fr;
    gap: 24px;
  }

  .blog-article {
    padding: 24px;
  }

  .sidebar-widget {
    margin-bottom: 24px;
  }
}

@media (max-width: 480px) {
  .blog-hero-title {
    font-size: 2rem;
  }

  .blog-article h2 {
    font-size: 1.2rem;
  }

  .pagination {
    flex-direction: column;
    gap: 12px;
  }

  .page-numbers {
    order: -1;
  }
}

public/css/blog-modern-layout.css.content:
/* Blog Modern Layout - DUATX Style */
:root {
  --duatx-bg: #0B0D10;
  --duatx-card: #1A1D23;
  --duatx-text: #E8E9EA;
  --duatx-text-muted: #9CA3AF;
  --duatx-orange: #AA5C2F;
  --duatx-blue: #2C6ECB;
  --duatx-light-blue: #7CC1DF;
  --duatx-green: #96B4A8;
  --duatx-border: #2A2D35;
  --duatx-hover: rgba(170, 92, 47, 0.1);
}

/* Reset and Base */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Montserrat', sans-serif;
  background-color: #000000;
  color: var(--duatx-text);
  line-height: 1.6;
  position: relative;
  overflow-x: hidden;
}

/* Blog Container */
.blog-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 120px 20px 60px;
  display: grid;
  grid-template-columns: 280px 1fr;
  gap: 40px;
  min-height: 100vh;
}

/* Blog Header */
.blog-header {
  grid-column: 1 / -1;
  margin-bottom: 40px;
}

.blog-title {
  font-size: 2.5rem;
  font-weight: 700;
  color: var(--duatx-text);
  margin-bottom: 10px;
}

.blog-breadcrumb {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.9rem;
  color: var(--duatx-text-muted);
}

.blog-breadcrumb a {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
  text-decoration: none;
  transition: all 0.3s ease;
}

.blog-breadcrumb a:hover {
  color: var(--duatx-light-blue);
}

/* Sidebar */
.blog-sidebar {
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 30px;
  height: fit-content;
  position: sticky;
  top: 140px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

/* Search Box */
.search-section {
  margin-bottom: 30px;
}

.search-box {
  position: relative;
}

.search-input {
  width: 100%;
  padding: 12px 16px 12px 40px;
  background: rgba(11, 13, 16, 0.6);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  color: var(--duatx-text);
  font-size: 0.95rem;
  transition: all 0.3s ease;
}

.search-input:focus {
  outline: none;
  border: 1px solid;
  border-image: linear-gradient(135deg, #AA5C2F 0%, #2C6ECB 100%) 1;
  box-shadow: 0 0 0 3px rgba(170, 92, 47, 0.1);
}

.search-input::placeholder {
  color: var(--duatx-text-muted);
}

.search-icon {
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY(-50%);
  width: 16px;
  height: 16px;
  color: var(--duatx-text-muted);
}

/* Categories */
.categories-section h3,
.top-posts-section h3 {
  font-size: 1.1rem;
  font-weight: 600;
  color: var(--duatx-text);
  margin-bottom: 16px;
}

.categories-list {
  list-style: none;
}

.categories-list li {
  margin-bottom: 8px;
}

.categories-list a {
  color: var(--duatx-text-muted);
  text-decoration: none;
  font-size: 0.95rem;
  transition: color 0.3s ease;
  display: block;
  padding: 4px 0;
}

.categories-list a:hover,
.categories-list a.active {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

/* Top Posts */
.top-posts-section {
  margin-top: 40px;
}

.top-posts-list {
  list-style: none;
}

.top-post-item {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  margin-bottom: 16px;
  padding: 12px 0;
  border-bottom: 1px solid var(--duatx-border);
}

.top-post-item:last-child {
  border-bottom: none;
}

.post-number {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  color: white;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.8rem;
  font-weight: 600;
  flex-shrink: 0;
}

.post-preview h4 {
  font-size: 0.9rem;
  font-weight: 500;
  color: var(--duatx-text);
  margin-bottom: 4px;
  line-height: 1.4;
}

.post-preview .post-meta {
  font-size: 0.8rem;
  color: var(--duatx-text-muted);
}

.post-preview a {
  text-decoration: none;
  color: inherit;
}

.post-preview a:hover h4 {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

/* Main Content */
.blog-main {
  min-height: 800px;
}

.articles-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 30px;
  margin-bottom: 50px;
}

/* Article Cards */
.article-card {
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
}

.article-card:hover {
  transform: translateY(-8px);
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.15);
  box-shadow: 0 16px 40px rgba(0, 0, 0, 0.25);
  border-color: rgba(170, 92, 47, 0.3);
}

.article-image {
  width: 100%;
  height: 220px;
  background: linear-gradient(135deg, var(--duatx-orange), var(--duatx-light-blue));
  position: relative;
  overflow: hidden;
}

.article-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.article-content {
  padding: 24px;
}

.article-category {
  display: inline-block;
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
  border: 1px solid;
  border-image: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%) 1;
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 600;
  margin-bottom: 12px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  position: relative;
}

.article-title {
  font-size: 1.3rem;
  font-weight: 600;
  color: var(--duatx-text);
  margin-bottom: 12px;
  line-height: 1.4;
}

.article-title a {
  color: inherit;
  text-decoration: none;
  transition: color 0.3s ease;
}

.article-title a:hover {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

.article-excerpt {
  color: var(--duatx-text-muted);
  font-size: 0.95rem;
  line-height: 1.6;
  margin-bottom: 16px;
}

.article-meta {
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 0.85rem;
  color: var(--duatx-text-muted);
}

.article-date {
  display: flex;
  align-items: center;
  gap: 6px;
}

.read-more {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
}

.read-more:hover {
  color: var(--duatx-light-blue);
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  margin-top: 50px;
}

.pagination-btn {
  padding: 10px 16px;
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  color: var(--duatx-text);
  text-decoration: none;
  font-size: 0.9rem;
  transition: all 0.3s ease;
}

.pagination-btn:hover,
.pagination-btn.active {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
  border-color: transparent;
  color: white;
}

/* Social Section */
.social-section {
  margin-top: 40px;
  padding-top: 30px;
  border-top: 1px solid var(--duatx-border);
}

/* Footer Social Media - Same as main site */
.footer-social {
  margin-top: 1.5rem;
  display: flex;
  gap: 1rem;
}

.social-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  background: transparent;
  border: 2px solid white;
  border-radius: 12px;
  color: white;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(255, 255, 255, 0.2);
}

.social-link:hover {
  transform: scale(1.1);
  box-shadow: 0 6px 20px rgba(255, 255, 255, 0.4);
  background: rgba(255, 255, 255, 0.1);
}

.social-link:active {
  transform: scale(0.95);
}

.social-link i {
  width: 1.25rem;
  height: 1.25rem;
}

.social-link:focus-visible {
  outline: 2px solid;
  outline-image: linear-gradient(135deg, #AA5C2F 0%, #2C6ECB 100%);
  outline-offset: 2px;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .blog-container {
    grid-template-columns: 1fr;
    gap: 30px;
    padding: 100px 20px 60px;
  }

  .blog-sidebar {
    position: static;
    margin-bottom: 40px;
  }

  .articles-grid {
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 20px;
  }
}

@media (max-width: 768px) {
  .blog-container {
    padding: 90px 15px 40px;
  }

  .blog-title {
    font-size: 2rem;
  }

  .articles-grid {
    grid-template-columns: 1fr;
  }

  .article-image {
    height: 180px;
  }

  .article-content {
    padding: 20px;
  }

  .instagram-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 480px) {
  .blog-sidebar {
    padding: 20px;
  }

  .article-title {
    font-size: 1.1rem;
  }

  .search-input {
    font-size: 0.9rem;
  }
}

/* Loading States */
.loading {
  opacity: 0.6;
  pointer-events: none;
}

.skeleton {
  background: linear-gradient(90deg, var(--duatx-border) 25%, transparent 50%, var(--duatx-border) 75%);
  background-size: 200% 100%;
  animation: loading 1.5s infinite;
}

@keyframes loading {
  0% { background-position: 200% 0; }
  100% { background-position: -200% 0; }
}

/* Success Animation */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.fade-in-up {
  animation: fadeInUp 0.6s ease-out;
}

public/css/blog-ultra-modern.css.content:
/* Blog Ultra Modern - Baseado na imagem de referência */

.blog-modern {
  background: #0B0D10;
  padding: 80px 0 120px;
  position: relative;
  overflow: hidden;
}

.blog-modern::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(ellipse at center, rgba(59, 124, 255, 0.08) 0%, transparent 70%);
  pointer-events: none;
}

.blog-modern .container {
  position: relative;
  z-index: 2;
}

/* Header */
.blog-header-modern {
  text-align: center;
  margin-bottom: 60px;
}

.blog-title-main {
  font-size: 3rem;
  font-weight: 700;
  color: #FFFFFF;
  margin-bottom: 20px;
  line-height: 1.2;
}

.blog-subtitle-main {
  font-size: 1.2rem;
  color: #B0B3B8;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
}

/* Controls */
.blog-controls-modern {
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 50px;
  gap: 40px;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
}

.search-container-modern {
  position: relative;
  width: 350px;
  flex-shrink: 0;
}

.search-input-modern {
  width: 100%;
  padding: 12px 16px 12px 45px;
  background: rgba(255, 255, 255, 0.08);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 12px;
  color: #FFFFFF;
  font-size: 14px;
  transition: all 0.3s ease;
}

.search-input-modern:focus {
  background: rgba(255, 255, 255, 0.12);
  border-color: #2C6ECB;
  outline: none;
  box-shadow: 0 0 0 3px rgba(44, 110, 203, 0.2);
}

.search-input-modern::placeholder {
  color: #8B9095;
}

.search-icon-modern {
  position: absolute;
  left: 16px;
  top: 50%;
  transform: translateY(-50%);
  color: #8B9095;
  width: 18px;
  height: 18px;
}

.filter-tabs-modern {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
}

.filter-tab-modern {
  padding: 10px 20px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  color: #B0B3B8;
  font-size: 13px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;
}

.filter-tab-modern:hover {
  background: rgba(255, 255, 255, 0.08);
  border-color: rgba(255, 255, 255, 0.2);
}

.filter-tab-modern.active {
  background: rgba(44, 110, 203, 0.2);
  border-color: #2C6ECB;
  color: #FFFFFF;
}

/* Grid Layout */
.blog-grid-modern {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 24px;
}

/* Cards */
.blog-card-modern {
  background: linear-gradient(180deg, #2A3C4E 0%, #1D1F22 50%, #2B2D31 100%);
  border: 1px solid rgba(255, 255, 255, 0.08);
  border-radius: 16px;
  padding: 28px;
  position: relative;
  transition: all 0.4s ease;
  backdrop-filter: blur(10px);
  min-height: 420px;
  display: flex;
  flex-direction: column;
}

.blog-card-modern:hover {
  transform: translateY(-8px);
  background: linear-gradient(180deg, #2C3645 0%, #1D1F22 50%, #2B2D31 100%);
  border-color: rgba(255, 255, 255, 0.15);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.4);
}

.blog-card-modern.featured {
  position: relative;
}

/* Hot Badge */
.blog-badge-hot {
  position: absolute;
  top: -5px;
  left: -3px;
  background: linear-gradient(130deg, #CC3A00 0%, #FF6A2D 100%);
  border: 1px solid #FF934D;
  color: #FFFFFF;
  padding: 6px 15px;
  border-radius: 13px;
  font-size: 11px;
  font-weight: 700;
  letter-spacing: 0.5px;
  z-index: 3;
  box-shadow: 0 0 8px rgba(255, 106, 45, 0.4);
}

.blog-badge-hot:hover {
  background: linear-gradient(90deg, #FF934D 0%, #CC3A00 100%);
  box-shadow: 0 0 12px rgba(255, 147, 77, 0.6);
  transform: translateY(-1px);
}

/* Icons */
.blog-icon-modern {
  width: 64px;
  height: 64px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 24px auto;
  position: relative;
  overflow: hidden;
}

.blog-icon-modern::before {
  content: '';
  position: absolute;
  inset: 0;
  border-radius: 12px;
  padding: 1px;
  background: linear-gradient(135deg, currentColor, transparent);
  -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: exclude;
  mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  mask-composite: exclude;
}

.blog-icon-modern svg {
  color: #FFFFFF;
  z-index: 2;
  position: relative;
}

.orange-gradient {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
}

.blue-gradient {
  background: linear-gradient(135deg, #3AAFF4 0%, #3B7CFF 100%);
}

.teal-gradient {
  background: linear-gradient(135deg, #59C4B6 0%, #61A8A1 100%);
}

.purple-gradient {
  background: linear-gradient(135deg, #5A6C89 0%, #2C3645 100%);
}

/* Categories */
.blog-category-modern {
  font-size: 11px;
  font-weight: 700;
  letter-spacing: 1px;
  margin-bottom: 16px;
  text-transform: uppercase;
}

.blog-category-modern.orange {
  color: #3AAFF4;
}

.blog-category-modern.blue {
  color: #59C4B6;
}

.blog-category-modern.teal {
  color: #61A8A1;
}

.blog-category-modern.purple {
  color: #5A6C89;
}

/* Small Tag */
.blog-tag-small {
  position: absolute;
  top: 16px;
  right: 16px;
  background: rgba(255, 255, 255, 0.1);
  color: #FFFFFF;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 10px;
  font-weight: 600;
}

/* Title */
.blog-card-title {
  font-size: 1.3rem;
  font-weight: 600;
  color: #FFFFFF;
  line-height: 1.4;
  margin-bottom: 16px;
  flex-grow: 1;
}

/* Meta */
.blog-meta-modern {
  display: flex;
  gap: 16px;
  margin-bottom: 16px;
  font-size: 12px;
  color: #8B9095;
}

.blog-meta-modern span {
  display: flex;
  align-items: center;
  gap: 6px;
}

.blog-meta-modern i {
  width: 14px;
  height: 14px;
}

/* Description */
.blog-description {
  color: #B0B3B8;
  line-height: 1.6;
  margin-bottom: 24px;
  font-size: 14px;
}

/* Buttons */
.blog-btn-modern {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  height: 40px;
  width: 130px;
  padding: 10px 10px;
  border-radius: 25px;
  font-weight: 600;
  font-size: 13px;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
  margin: auto auto 0 auto;
  color: #FFFFFF;
}

.blog-btn-modern.orange {
  background: linear-gradient(90deg, #F26A30 0%, #1F1F1F 100%);
  box-shadow: 0 4px 12px rgba(242, 106, 48, 0.25);
}

.blog-btn-modern.orange:hover {
  background: linear-gradient(135deg, #FF934D 0%, #1f1f1f 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(242, 106, 48, 0.4);
}

.blog-btn-modern.blue {
  background: linear-gradient(90deg, #3B7CFF 0%, #1A1F2B 100%);
  box-shadow: 0 4px 12px rgba(59, 124, 255, 0.25);
}

.blog-btn-modern.blue:hover {
  background: linear-gradient(135deg, #5A94FF 0%, #1A1F2B 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(59, 124, 255, 0.4);
}

.blog-btn-modern i {
  width: 16px;
  height: 16px;
  transition: transform 0.3s ease;
}

.blog-btn-modern:hover i {
  transform: translateX(3px);
}

/* Responsive */
@media (max-width: 1200px) {
  .blog-grid-modern {
    grid-template-columns: repeat(3, 1fr);
  }
}

@media (max-width: 768px) {
  .blog-title-main {
    font-size: 2.2rem;
  }

  .blog-controls-modern {
    flex-direction: column;
    gap: 24px;
    padding: 0 20px;
  }

  .search-container-modern {
    width: 100%;
    max-width: 400px;
  }

  .filter-tabs-modern {
    justify-content: center;
    width: 100%;
  }

  .blog-grid-modern {
    grid-template-columns: repeat(2, 1fr);
    gap: 20px;
  }

  .blog-card-modern {
    padding: 24px;
    min-height: 380px;
  }
}

@media (max-width: 480px) {
  .blog-grid-modern {
    grid-template-columns: 1fr;
  }

  .blog-card-modern {
    padding: 20px;
  }

  .blog-title-main {
    font-size: 1.8rem;
  }

  .filter-tabs-modern {
    overflow-x: auto;
    justify-content: flex-start;
    padding-bottom: 8px;
  }

  .filter-tab-modern {
    flex-shrink: 0;
  }
}

/* Animation enhancements */
@keyframes slideInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.blog-card-modern {
  animation: slideInUp 0.6s ease forwards;
}

.blog-card-modern:nth-child(1) {
  animation-delay: 0.1s;
}

.blog-card-modern:nth-child(2) {
  animation-delay: 0.2s;
}

.blog-card-modern:nth-child(3) {
  animation-delay: 0.3s;
}

.blog-card-modern:nth-child(4) {
  animation-delay: 0.4s;
}

server/index.ts.content:
// TypeScript entry point for the Express server
import express from 'express';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Serve static files from the public directory
app.use(express.static(path.join(__dirname, '../public')));

// API logging middleware
app.use((req, res, next) => {
  const start = Date.now();
  const reqPath = req.path;
  let capturedJsonResponse = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (reqPath.startsWith("/api")) {
      let logLine = `${req.method} ${reqPath} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      console.log(logLine);
    }
  });

  next();
});

// API endpoint for contact form
app.post('/api/contact', (req, res) => {
  const { name, email, phone, company, message } = req.body;

  // Log the contact form submission
  console.log('Contact form submission:', {
    name,
    email,
    phone,
    company,
    message,
    timestamp: new Date().toISOString()
  });

  res.json({ 
    success: true, 
    message: 'Mensagem recebida com sucesso! Entraremos em contato em breve.' 
  });
});

// API endpoint for chatbot/agent interactions
app.post('/api/chat', (req, res) => {
  const { message, context } = req.body;

  // Log the chat interaction
  console.log('Chat interaction:', {
    message,
    context,
    timestamp: new Date().toISOString()
  });

  // Portuguese-only responses for the agent
  const responses = {
    greeting: "Olá! Sou o assistente virtual da DUATX. Como posso ajudá-lo hoje? Posso falar sobre nossos serviços de IA, automação, dashboards e casos de sucesso.",
    services: "A DUATX oferece soluções em: 1) Inteligência Artificial e Machine Learning, 2) Automação de Processos, 3) Dashboards e Análise de Dados, 4) Software sob Medida. Sobre qual serviço gostaria de saber mais?",
    sectors: "Atuamos em diversos setores: Indústria, Saúde, Logística, Jurídico, Varejo e Serviços. Cada setor tem suas particularidades e desenvolvemos soluções específicas para maximizar os resultados.",
    contact: "Para agendar um diagnóstico gratuito, você pode: 1) Usar o WhatsApp: (11) 91290-6767, 2) Preencher o formulário de contato no site, 3) Enviar email para contato@duatx.com.br",
    default: "Entendo sua pergunta. Para melhor atendê-lo, posso explicar sobre nossos serviços, setores de atuação, casos de sucesso ou como agendar um diagnóstico gratuito. O que seria mais útil para você?"
  };

  // Simple keyword-based response logic (em português)
  const messageText = message.toLowerCase();
  let response = responses.default;

  if (messageText.includes('olá') || messageText.includes('oi') || messageText.includes('bom dia') || messageText.includes('boa tarde')) {
    response = responses.greeting;
  } else if (messageText.includes('serviço') || messageText.includes('solução') || messageText.includes('ia') || messageText.includes('automação')) {
    response = responses.services;
  } else if (messageText.includes('setor') || messageText.includes('indústria') || messageText.includes('saúde') || messageText.includes('logística')) {
    response = responses.sectors;
  } else if (messageText.includes('contato') || messageText.includes('agendar') || messageText.includes('diagnóstico') || messageText.includes('whatsapp')) {
    response = responses.contact;
  }

  res.json({
    success: true,
    response: response,
    timestamp: new Date().toISOString()
  });
});

// Serve the main HTML file for all routes (SPA behavior)
app.get('*', (req, res) => {
  res.sendFile(path.join(__dirname, '../public/index.html'));
});

// Error handling middleware
app.use((err: any, _req: any, res: any, _next: any) => {
  const status = err.status || err.statusCode || 500;
  const message = err.message || "Internal Server Error";

  res.status(status).json({ message });
  console.error(err);
});

const PORT = parseInt(process.env.PORT || '5000');
app.listen(PORT, "0.0.0.0", () => {
  console.log(`Server running on port ${PORT}`);
  console.log(`Website accessible at http://localhost:${PORT}`);
});

public/css/stars-fix.css.content:
/* Force stars on all sections with maximum specificity */
section[id="about"]::after,
section[id="services"]::after,
section[id="sectors"]::after,
section[id="technologies"]::after,
section[id="results"]::after,
section[id="testimonials"]::after,
section[id="blog"]::after {
  content: '' !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
  right: 0 !important;
  bottom: 0 !important;
  background: 
    radial-gradient(1px 1px at 120px 80px, rgba(255, 255, 255, 0.4), transparent 2px),
    radial-gradient(0.8px 0.8px at 280px 200px, rgba(255, 255, 255, 0.3), transparent 1.5px),
    radial-gradient(1.2px 1.2px at 400px 150px, rgba(255, 255, 255, 0.35), transparent 2px),
    radial-gradient(0.6px 0.6px at 60px 280px, rgba(255, 255, 255, 0.25), transparent 1px),
    radial-gradient(1px 1px at 350px 60px, rgba(255, 255, 255, 0.4), transparent 2px),
    radial-gradient(0.7px 0.7px at 180px 340px, rgba(255, 255, 255, 0.3), transparent 1.5px),
    radial-gradient(1.1px 1.1px at 30px 180px, rgba(255, 255, 255, 0.35), transparent 2px),
    radial-gradient(0.5px 0.5px at 380px 280px, rgba(255, 255, 255, 0.2), transparent 1px) !important;
  background-repeat: repeat !important;
  background-size: 500px 400px, 600px 500px, 550px 450px, 450px 350px, 650px 550px, 500px 400px, 400px 300px, 700px 600px !important;
  animation: gentleStarTwinkle 15s ease-in-out infinite !important;
  z-index: 999 !important;
  pointer-events: none !important;
}

