    font-weight: 700;
    letter-spacing: 0.5px;
    text-transform: uppercase;
}

/* Icons */
.blog-icon-final {
    width: 64px;
    height: 64px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 24px;
    margin-top: 8px;
}

.blog-icon-final.orange {
    background: #FF6B47;
}

.blog-icon-final.blue {
    background: #5B9BD5;
}

.blog-icon-final.teal {
    background: #70C7BA;
}

.blog-icon-final.dark {
    background: #4A5568;
}

.blog-icon-final svg {
    color: white;
}

/* Category and Tag */
.blog-category-final {
    color: rgba(255, 255, 255, 0.7);
    font-size: 11px;
    font-weight: 600;
    letter-spacing: 1px;
    text-transform: uppercase;
    margin-bottom: 8px;
}

.blog-tag-final {
    display: inline-block;
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    padding: 2px 8px;
    border-radius: 8px;
    font-size: 10px;
    font-weight: 600;
    margin-left: 8px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Title */
.blog-title-final {
    color: white;
    font-size: 18px;
    font-weight: 700;
    line-height: 1.3;
    margin-bottom: 16px;
    font-family: 'Montserrat', sans-serif;
}

/* Meta */
.blog-meta-final {
    display: flex;
    gap: 16px;
    margin-bottom: 16px;
    color: rgba(255, 255, 255, 0.5);
    font-size: 12px;
}

.blog-meta-final span {
    display: flex;
    align-items: center;
    gap: 4px;
}

.blog-meta-final i {
    width: 12px;
    height: 12px;
}

/* Description */
.blog-desc-final {
    color: rgba(255, 255, 255, 0.7);
    font-size: 14px;
    line-height: 1.5;
    margin-bottom: 24px;
    flex-grow: 1;
}

/* Buttons */
.blog-btn-final {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    font-size: 14px;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s ease;
    padding: 12px 20px;
    border-radius: 8px;
    margin-top: auto;
    align-self: flex-start;
    color: white;
}

.blog-btn-final.orange {
    background: #FF6B47;
}

.blog-btn-final.blue {
    background: #5B9BD5;
}

.blog-btn-final:hover {
    transform: translateX(2px);
    opacity: 0.9;
}

.blog-btn-final i {
    width: 14px;
    height: 14px;
    transition: transform 0.3s ease;
}

.blog-btn-final:hover i {
    transform: translateX(2px);
}

/* Responsive Design */
@media (max-width: 1024px) {
    .blog-controls-exact {
        flex-direction: column;
        align-items: stretch;
        gap: 16px;
    }

    .blog-search-left {
        min-width: auto;
        max-width: 400px;
        margin: 0 auto;
    }

    .blog-filters-right {
        justify-content: center;
    }

    .blog-carousel-final {
        gap: 20px;
    }

    .blog-card-final {
        min-width: 260px;
    }
}

@media (max-width: 768px) {
    .blog-controls-exact {
        padding: 0 16px;
    }

    .blog-carousel-wrapper-final {
        padding: 0 16px;
    }

    .blog-carousel-final {
        gap: 16px;
    }

    .blog-card-final {
        min-height: 380px;
        min-width: 240px;
        padding: 20px;
    }

    .blog-title-final {
        font-size: 16px;
    }

    .blog-filters-right {
        justify-content: flex-start;
        overflow-x: auto;
        flex-wrap: nowrap;
        padding-bottom: 8px;
    }

    .filter-btn-exact {
        flex-shrink: 0;
    }
}

@media (max-width: 480px) {
    .blog-card-final {
        padding: 16px;
        min-height: 360px;
    }

    .blog-icon-final {
        width: 56px;
        height: 56px;
    }

    .blog-icon-final svg {
        width: 32px;
        height: 32px;
    }

    .search-input-exact {
        padding: 10px 16px 10px 40px;
        font-size: 13px;
    }

    .search-icon-exact {
        left: 14px;
        width: 14px;
        height: 14px;
    }
}

/* Blog Carousel Layout */
.carousel-wrapper-blog {
    position: relative;
    overflow: hidden;
    max-width: 1200px;
    margin: 0 auto;
}

.blog-carousel-modern {
    display: flex;
    gap: 24px;
    transition: transform 0.3s ease;
    will-change: transform;
}

/* Navigation */
.carousel-nav-blog {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 20px;
    margin-top: 2rem;
}

.carousel-btn-blog {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    width: 48px;
    height: 48px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    cursor: pointer;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
}

.carousel-btn-blog:hover {
    background: rgba(255, 255, 255, 0.2);
    border-color: rgba(255, 255, 255, 0.3);
    transform: scale(1.05);
}

.carousel-btn-blog:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

.carousel-btn-blog i {
    width: 20px;
    height: 20px;
}

.carousel-dots-blog {
    display: flex;
    gap: 8px;
}

.dot-blog {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    cursor: pointer;
    transition: all 0.3s ease;
}

.dot-blog.active {
    background: #FF6B35;
    transform: scale(1.2);
}

/* Exact Blog Cards */
.blog-card-exact {
    background: linear-gradient(145deg, rgba(55, 65, 81, 0.8) 0%, rgba(45, 55, 72, 0.6) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 16px;
    padding: 24px;
    position: relative;
    transition: all 0.3s ease;
    min-height: 420px;
    min-width: 280px;
    flex-shrink: 0;
    display: flex;
    flex-direction: column;
    backdrop-filter: blur(10px);
}

.blog-card-exact:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.4);
}

.blog-card-exact.featured {
    background: linear-gradient(145deg, rgba(68, 64, 60, 0.8) 0%, rgba(45, 55, 72, 0.4) 100%);
}

/* Status Badge */
.blog-status-exact {
    position: absolute;
    top: 16px;
    left: 16px;
    background: #FF6B35;
    color: white;
    padding: 4px 12px;
    border-radius: 12px;
    font-size: 10px;
    font-weight: 700;
    letter-spacing: 0.5px;
    text-transform: uppercase;
}

/* Exact Icon Styles */
.blog-icon-exact {
    width: 64px;
    height: 64px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 24px;
    margin-top: 8px;
}

.blog-icon-exact.coral {
    background: #FF6B47;
}

.blog-icon-exact.lightblue {
    background: #5B9BD5;
}

.blog-icon-exact.teal {
    background: #70C7BA;
}

.blog-icon-exact.darkblue {
    background: #4A5568;
}

.blog-icon-exact svg {
    color: white;
}

/* Category */
.blog-category-exact {
    color: rgba(255, 255, 255, 0.7);
    font-size: 11px;
    font-weight: 600;
    letter-spacing: 1px;
    text-transform: uppercase;
    margin-bottom: 8px;
}

.blog-category-tag {
    display: inline-block;
    background: rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.8);
    padding: 2px 8px;
    border-radius: 8px;
    font-size: 10px;
    font-weight: 600;
    margin-left: 8px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Title */
.blog-title-exact {
    color: white;
    font-size: 18px;
    font-weight: 700;
    line-height: 1.4;
    margin-bottom: 16px;
    font-family: 'Montserrat', sans-serif;
    flex-grow: 1;
}

/* Meta */
.blog-meta-exact {
    display: flex;
    gap: 16px;
    margin-bottom: 16px;
    color: rgba(255, 255, 255, 0.5);
    font-size: 12px;
}

.blog-time-exact,
.blog-date-exact {
    display: flex;
    align-items: center;
    gap: 4px;
}

.blog-time-exact i,
.blog-date-exact i {
    width: 12px;
    height: 12px;
}

/* Description */
.blog-desc-exact {
    color: rgba(255, 255, 255, 0.7);
    font-size: 14px;
    line-height: 1.5;
    margin-bottom: 24px;
    flex-grow: 1;
}

/* CTA Button */
.blog-btn-exact {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    font-size: 14px;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s ease;
    padding: 12px 20px;
    border-radius: 8px;
    margin-top: auto;
    align-self: flex-start;
    color: white;
}

/* Botão laranja para cards ímpares (1º, 3º, 5º) */
.blog-card-exact:nth-child(odd) .blog-btn-exact {
    background: #FF6B47;
}

/* Botão azul para cards pares (2º, 4º, 6º) */
.blog-card-exact:nth-child(even) .blog-btn-exact {
    background: #5B9BD5;
}

.blog-btn-exact:hover {
    transform: translateX(2px);
    opacity: 0.9;
}

.blog-btn-exact i {
    width: 14px;
    height: 14px;
    transition: transform 0.3s ease;
}

.blog-btn-exact:hover i {
    transform: translateX(2px);
}

/* Responsive Design */
@media (max-width: 1024px) {
    .blog-controls-container {
        flex-direction: column;
        align-items: stretch;
        gap: 16px;
    }

    .blog-filters-left {
        justify-content: center;
    }

    .blog-search-right {
        min-width: auto;
        max-width: 400px;
        margin: 0 auto;
    }
}

@media (max-width: 768px) {
    .blog-card-exact {
        min-height: 380px;
        min-width: 260px;
        padding: 20px;
    }

    .blog-title-exact {
        font-size: 16px;
    }

    .blog-controls-container {
        padding: 0 16px;
    }

    .blog-filters-left {
        justify-content: flex-start;
        overflow-x: auto;
        flex-wrap: nowrap;
        padding-bottom: 8px;
    }

    .filter-button {
        white-space: nowrap;
        flex-shrink: 0;
    }

    .carousel-wrapper-blog {
        padding: 0 16px;
    }
}

@media (max-width: 480px) {
    .blog-card-exact {
        padding: 16px;
        min-height: 360px;
        min-width: 240px;
    }

    .blog-icon-exact {
        width: 56px;
        height: 56px;
    }

    .blog-controls-container {
        padding: 0 12px;
    }

    .modern-search-container {
        padding: 0;
    }

    .search-input-modern {
        padding: 14px 18px 14px 45px;
        font-size: 14px;
    }

    .carousel-wrapper-blog {
        padding: 0 12px;
    }
}

public/css/main.css.content:
/* Global Overflow Control */
html,
body {
  overflow-x: hidden;
  max-width: 100vw;
  margin: 0;
  padding: 0;
  font-family: "Montserrat", system-ui, -apple-system, BlinkMacSystemFont,
    "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  background-color: #000000;
  background-image: url('../assets/BG9.png');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-attachment: fixed;
  color: #ffffff;
  line-height: 1.6;
}

/* LGPD Cookie Banner Styles */
.cookie-banner {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 9999;
  background: linear-gradient(145deg, 
    hsl(220, 25%, 8%) 0%,
    hsl(220, 20%, 10%) 50%,
    hsl(220, 25%, 8%) 100%);
  backdrop-filter: blur(20px);
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 
    0 -8px 32px rgba(0, 0, 0, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.05),
    0 -1px 0 rgba(0, 0, 0, 0.2);
  animation: slideUpCookie 0.5s ease-out forwards;
  padding: 0;
  display: none;
}

@keyframes slideUpCookie {
  from {
    transform: translateY(100%);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.cookie-banner-content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 24px 32px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 24px;
}

.cookie-banner-text {
  display: flex;
  align-items: flex-start;
  gap: 16px;
  flex: 1;
}

.cookie-icon {
  flex-shrink: 0;
  width: 40px;
  height: 40px;
  background: linear-gradient(145deg, hsl(220, 25%, 12%), hsl(220, 25%, 8%));
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 
    inset 2px 2px 5px rgba(0, 0, 0, 0.3),
    inset -2px -2px 5px rgba(255, 255, 255, 0.02);
}

.cookie-icon i {
  color: #AA5C2F;
  width: 20px;
  height: 20px;
}

.cookie-content {
  flex: 1;
}

.cookie-title {
  color: #FFFFFF;
  font-size: 18px;
  font-weight: 600;
  margin: 0 0 8px 0;
  font-family: 'Montserrat', sans-serif;
}

.cookie-description {
  color: rgba(255, 255, 255, 0.8);
  font-size: 14px;
  line-height: 1.5;
  margin: 0 0 12px 0;
  font-family: 'Montserrat', sans-serif;
}

.cookie-links {
  margin-top: 8px;
}

.cookie-policy-link {
  color: #2C6ECB;
  text-decoration: underline;
  font-size: 13px;
  transition: color 0.3s ease;
  font-family: 'Montserrat', sans-serif;
}

.cookie-policy-link:hover {
  color: #4A90E2;
}

.cookie-buttons {
  display: flex;
  gap: 12px;
  flex-shrink: 0;
}

.cookie-btn {
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  font-family: 'Montserrat', sans-serif;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.cookie-btn-primary {
  background: linear-gradient(145deg, #AA5C2F, #C76A35);
  color: #FFFFFF;
  box-shadow: 
    0 4px 15px rgba(170, 92, 47, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.2),
    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
}

.cookie-btn-primary:hover {
  background: linear-gradient(145deg, #C76A35, #D4763D);
  box-shadow: 
    0 6px 20px rgba(170, 92, 47, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.3),
    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
  transform: translateY(-1px);
}

.cookie-btn-secondary {
  background: linear-gradient(145deg, hsl(220, 25%, 15%), hsl(220, 25%, 12%));
  color: rgba(255, 255, 255, 0.9);
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 
    0 4px 15px rgba(0, 0, 0, 0.2),
    inset 0 1px 0 rgba(255, 255, 255, 0.05),
    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
}

.cookie-btn-secondary:hover {
  background: linear-gradient(145deg, hsl(220, 25%, 18%), hsl(220, 25%, 15%));
  border-color: rgba(255, 255, 255, 0.2);
  box-shadow: 
    0 6px 20px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.08),
    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
  transform: translateY(-1px);
}

/* Cookie Preferences Modal */
.cookie-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 10000;
  display: flex;
  align-items: center;
  justify-content: center;
  animation: fadeInModal 0.3s ease-out;
}

.cookie-modal-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(8px);
}

.cookie-modal-content {
  position: relative;
  background: linear-gradient(145deg, 
    hsl(220, 25%, 10%) 0%,
    hsl(220, 20%, 12%) 50%,
    hsl(220, 25%, 10%) 100%);
  border-radius: 16px;
  max-width: 600px;
  width: 90%;
  max-height: 80vh;
  overflow-y: auto;
  box-shadow: 
    0 20px 60px rgba(0, 0, 0, 0.5),
    inset 0 1px 0 rgba(255, 255, 255, 0.1),
    0 1px 0 rgba(0, 0, 0, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
  animation: scaleInModal 0.3s ease-out;
}

@keyframes fadeInModal {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes scaleInModal {
  from {
    transform: scale(0.9);
    opacity: 0;
  }
  to {
    transform: scale(1);
    opacity: 1;
  }
}

.cookie-modal-header {
  padding: 32px 32px 0;
  display: flex;
  align-items: center;
  justify-content: space-between;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  margin-bottom: 32px;
}

.cookie-modal-header h2 {
  color: #FFFFFF;
  font-size: 24px;
  font-weight: 600;
  margin: 0;
  font-family: 'Montserrat', sans-serif;
}

.cookie-modal-close {
  background: linear-gradient(145deg, hsl(220, 25%, 15%), hsl(220, 25%, 12%));
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 
    inset 2px 2px 5px rgba(0, 0, 0, 0.2),
    inset -2px -2px 5px rgba(255, 255, 255, 0.02);
}

.cookie-modal-close:hover {
  background: linear-gradient(145deg, hsl(220, 25%, 18%), hsl(220, 25%, 15%));
  border-color: rgba(255, 255, 255, 0.2);
}

.cookie-modal-close i {
  color: rgba(255, 255, 255, 0.7);
  width: 20px;
  height: 20px;
}

.cookie-modal-body {
  padding: 0 32px 32px;
}

.cookie-modal-intro {
  color: rgba(255, 255, 255, 0.8);
  font-size: 14px;
  line-height: 1.6;
  margin-bottom: 32px;
  font-family: 'Montserrat', sans-serif;
}

.cookie-category {
  margin-bottom: 24px;
  padding: 20px;
  background: linear-gradient(145deg, hsl(220, 25%, 8%), hsl(220, 25%, 12%));
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.05);
  box-shadow: 
    inset 2px 2px 5px rgba(0, 0, 0, 0.1),
    inset -2px -2px 5px rgba(255, 255, 255, 0.02);
}

.cookie-category-header {
  display: flex;
  align-items: flex-start;
  gap: 16px;
}

.cookie-toggle-container {
  flex-shrink: 0;
  margin-top: 2px;
}

.cookie-switch {
  position: relative;
  display: inline-block;
  width: 52px;
  height: 28px;
  cursor: pointer;
}

.cookie-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.cookie-switch-slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(145deg, hsl(220, 25%, 15%), hsl(220, 25%, 12%));
  transition: 0.3s;
  border-radius: 28px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 
    inset 2px 2px 5px rgba(0, 0, 0, 0.2),
    inset -2px -2px 5px rgba(255, 255, 255, 0.02);
}

.cookie-switch-slider:before {
  position: absolute;
  content: "";
  height: 20px;
  width: 20px;
  left: 3px;
  bottom: 3px;
  background: linear-gradient(145deg, #FFFFFF, #F0F0F0);
  transition: 0.3s;
  border-radius: 50%;
  box-shadow: 
    0 2px 5px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);
}

input:checked + .cookie-switch-slider {
  background: linear-gradient(145deg, #AA5C2F, #C76A35);
  border-color: rgba(255, 255, 255, 0.2);
}

input:checked + .cookie-switch-slider:before {
  transform: translateX(24px);
}

input:disabled + .cookie-switch-slider {
  opacity: 0.6;
  cursor: not-allowed;
}

.cookie-category-info h4 {
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
  font-family: 'Montserrat', sans-serif;
}

.cookie-category-info p {
  color: rgba(255, 255, 255, 0.7);
  font-size: 13px;
  line-height: 1.5;
  margin: 0;
  font-family: 'Montserrat', sans-serif;
}

.cookie-modal-footer {
  padding: 24px 32px 32px;
  display: flex;
  gap: 16px;
  justify-content: flex-end;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
}

/* Mobile Responsive */
@media (max-width: 768px) {
  .cookie-banner-content {
    flex-direction: column;
    align-items: flex-start;
    padding: 20px;
    gap: 20px;
  }

  .cookie-banner-text {
    flex-direction: column;
    gap: 12px;
  }

  .cookie-icon {
    align-self: flex-start;
  }

  .cookie-buttons {
    align-self: stretch;
    flex-direction: column;
  }

  .cookie-btn {
    width: 100%;
    justify-content: center;
