  min-height: 80px;
}

@media (max-width: 768px) {
  .section-padding {
    padding: 4rem 1rem;
  }
}

/* Hero Section Improvements */
.hero-section {
  padding: 0rem 1.25rem;
}

.hero-content .buttons {
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-top: 2rem;
}

.hero-content p {
  margin-bottom: 2rem;
}

/* Typography - Improved Hierarchy */
h1,
h2,
h3,
h4,
h5,
h6,
.section-title {
  font-family: 'Montserrat', sans-serif;
  font-weight: 800;
  letter-spacing: -0.02em;
  line-height: 1.2;
}

.section-title {
  font-size: 2.5rem;
  font-weight: 800;
  text-align: center;
  margin-bottom: 2rem;
  color: white;
}

@media (min-width: 640px) {
  .section-title {
    font-size: 3rem;
  }
}

@media (min-width: 768px) {
  .section-title {
    font-size: 3.5rem;
  }
}

.section-subtitle {
  font-family: 'Montserrat', sans-serif;
  font-size: 1.125rem;
  font-weight: 400;
  color: #CFCFCD;
  text-align: center;
}

.section-subtitle-large {
  font-family: 'Montserrat', sans-serif;
  font-size: 1.375rem;
  font-weight: 400;
  color: #CFCFCD;
  text-align: center;
  max-width: 48rem;
  margin: 0 auto 2rem;
  line-height: 1.6;
  letter-spacing: -0.01em;
}

.section-header {
  margin-bottom: 5rem;
  margin-top: 6rem;
}

/* Text Elements */
p,
.text-body {
  font-family: 'Montserrat', sans-serif;
  font-size: 1.125rem;
  font-weight: 400;
  line-height: 1.6;
  color: #CFCFCD;
  letter-spacing: -0.005em;
}

.text-large {
  font-family: 'Montserrat', sans-serif;
  font-size: 1.125rem;
  font-weight: 500;
  line-height: 1.6;
}

.text-small {
  font-family: 'Montserrat', sans-serif;
  font-size: 0.875rem;
  font-weight: 400;
}

.text-bold {
  font-weight: 600;
}

.text-semibold {
  font-weight: 500;
}

/* Hero Typography */
.hero-tagline {
  font-family: 'Montserrat', sans-serif;
  font-size: 0.75rem;
  font-weight: 500;
  color: #CFCFCD;
  text-align: center;
  margin: 0 auto 1.5rem auto;
  line-height: 1.2;
  letter-spacing: -0.01em;
