/* Hero Logo */
.hero-logo {
  text-align: center;
  margin-bottom: 4rem;
  margin-top: -4rem;
  margin-left: 0;
  margin-right: 0;
  position: relative;
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
}

.hero-logo-img {
  height: 10rem;
  width: auto;
  max-width: 90%;
  object-fit: contain;
  filter: drop-shadow(0 0 30px rgba(255, 255, 255, 0.3));
  transition: all 0.3s ease;
  display: block;
  margin: 0 auto;
}

.hero-logo-img:hover {
