/* Text Gradients with Spectral Colors */
.text-gradient {
  background: linear-gradient(135deg,
      hsl(var(--spectrum-cyan)) 0%,
      hsl(var(--spectrum-blue)) 50%,
      hsl(var(--spectrum-orange)) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: textShimmer 3s ease-in-out infinite;
}

.text-foreground {
  color: hsl(var(--foreground));
}

/* Typewriter Effect */
.typewriter-text {
  position: relative;
  overflow: hidden;
}

.typewriter-text::after {
  content: '|';
  color: hsl(var(--spectrum-cyan));
  animation: blink 1s infinite;
  font-weight: normal;
}

@keyframes blink {

  0%,
  50% {
    opacity: 1;
  }

  51%,
  100% {
    opacity: 0;
  }
}

@keyframes textShimmer {

  0%,
  100% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }
}

/* Neomorphic Effects */
.neomorphic {
  box-shadow: inset 8px 8px 16px rgba(0, 0, 0, 0.3), inset -8px -8px 16px rgba(255, 255, 255, 0.05);
}

.neomorphic-elevated {
  box-shadow: 8px 8px 16px rgba(0, 0, 0, 0.4), -8px -8px 16px rgba(255, 255, 255, 0.05);
}

.neomorphic-pressed {
  box-shadow: inset 8px 8px 16px rgba(0, 0, 0, 0.4), inset -8px -8px 16px rgba(255, 255, 255, 0.05);
}

.neomorphic-card {
  background-color: hsl(var(--card));
  border-radius: 1.5rem;
  padding: 2rem;
  box-shadow: 8px 8px 16px rgba(0, 0, 0, 0.4), -8px -8px 16px rgba(255, 255, 255, 0.05);
  transition: all 0.3s ease;
}

.neomorphic-card:hover {
  transform: translateY(-4px);
  box-shadow: 12px 12px 24px rgba(0, 0, 0, 0.5), -12px -12px 24px rgba(255, 255, 255, 0.08);
}

/* Button Hierarchy - Primary and Secondary */
.btn,
.btn-primary,
.btn-cta-primary,
button.primary {
  background: linear-gradient(135deg, #4A5568 0%, #2D3748 100%);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 20px;
  font-weight: 500;
  font-size: 0.9rem;
  font-family: 'Montserrat', sans-serif;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  text-decoration: none;
  letter-spacing: 0.02em;
  position: relative;
  overflow: hidden;
  box-shadow: 0 4px 12px rgba(74, 85, 104, 0.4);
}

.btn-secondary,
.btn-cta-secondary,
button.secondary {
  background: transparent;
  color: #CFCFCD;
  border: 1px solid #707070;
  padding: 1rem 2rem;
  border-radius: 20px;
  font-weight: 500;
  font-size: 0.9rem;
  font-family: 'Montserrat', sans-serif;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  text-decoration: none;
  letter-spacing: 0.02em;
  position: relative;
  overflow: hidden;
}

/* Primary Button Hover States */
.btn:hover,
.btn-primary:hover,
.btn-cta-primary:hover,
button.primary:hover {
  background: linear-gradient(135deg, #5A6578 0%, #3D4A58 100%);
  transform: translateY(-2px);
  box-shadow: 0 6px 16px rgba(74, 85, 104, 0.5);
}

/* Secondary Button Hover States */
.btn-secondary:hover,
.btn-cta-secondary:hover,
button.secondary:hover {
  background: rgba(255, 255, 255, 0.05);
  border-color: #CFCFCD;
  color: white;
  transform: translateY(-2px);
}

/* Active States */
.btn:active,
.btn-primary:active,
.btn-cta-primary:active,
.btn-secondary:active,
.btn-cta-secondary:active,
button:active {
  transform: translateY(0);
}

.btn-large {
  padding: 1.25rem 2.5rem;
  font-size: 1rem;
  border-radius: 24px;
}

.btn-full {
  width: 100%;
}

/* Icon Buttons */
.btn-icon-only {
  width: 3.5rem;
  height: 3.5rem;
  padding: 0;
  border-radius: 18px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

/* Button Icons */
.btn-icon {
  width: 1rem;
  height: 1rem;
  transition: transform 0.3s ease;
}

.btn:hover .btn-icon,
.btn-primary:hover .btn-icon,
.btn-cta-primary:hover .btn-icon {
  transform: translateX(2px);
}

/* Navigation Bar */
.navbar {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background-color: transparent;
  box-shadow: none;
  z-index: 1000;
  padding: 20px 40px;
  transition: background-color 0.3s ease, box-shadow 0.3s ease;
}

.navbar.transparent {
  background-color: transparent;
  box-shadow: none;
}

.navbar.scrolled {
  background-color: transparent;
  box-shadow: none;
}

/* Hero content img styling */
.hero-content img {
  height: 10rem;
  width: auto;
  max-width: 90%;
  object-fit: contain;
  filter: drop-shadow(0 0 30px rgba(255, 255, 255, 0.3));
  transition: all 0.3s ease;
  margin-bottom: 1rem;
}

@media (max-width: 768px) {
  .hero-content img {
    height: 8rem;
  }
}

@media (max-width: 480px) {
  .hero-content img {
    height: 6rem;
  }
}

/* Hero tagline */
.hero-tagline {
  margin-top: 2rem;
  text-align: center;
}

.tagline-text {
  font-size: clamp(12px, 1.8vw, 20px);
  color: #ffffff;
  font-family: 'Montserrat', sans-serif;
  font-weight: 300;
  line-height: 1.2;
  margin: 0;
  white-space: nowrap;
  overflow: hidden;
  border-right: 2px solid transparent;
  width: 0;
  animation: typewriter 4s steps(80) 1s forwards;
  max-width: 95vw;
}

@keyframes typewriter {
  from {
    width: 0;
  }
  to {
    width: 100%;
  }
}

@media (max-width: 768px) {
  .tagline-text {
    white-space: normal;
    width: auto;
    animation: fadeInUp 1.5s ease-out 0.5s forwards;
  }
}

@keyframes fadeInUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.company-description {
  font-size: 18px;
  line-height: 1.6;
  color: #ffffff;
  text-align: center;
  margin: 3rem auto 4rem;
  max-width: 900px;
  padding: 0 2rem;
  font-family: 'Montserrat', sans-serif;
  font-weight: 300;
}

@media (max-width: 768px) {
  .company-description {
    font-size: 16px;
    margin: 2rem auto 3rem;
    padding: 0 1rem;
  }
}

.navbar-container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

/* Logo */
.logo {
  display: flex;
  align-items: center;
}

.logo-link {
  display: flex;
  align-items: center;
  text-decoration: none;
  cursor: pointer;
}

.logo-img {
  height: 4rem;
  width: auto;
  filter: brightness(0) invert(1);
  transition: all 0.3s ease;
}

.logo-link:hover .logo-img {
  filter: brightness(0) invert(1) drop-shadow(0 0 8px rgba(255, 255, 255, 0.5));
  transform: scale(1.05);
}

/* Menu Navigation */
.menu {
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  gap: 1.5rem;
}

.menu li {
  margin: 0;
}

.menu a {
  color: rgba(255, 255, 255, 0.8);
  text-decoration: none;
  font-size: 0.8rem;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  transition: color 0.3s ease;
  font-family: 'Montserrat', sans-serif;
}

.menu a:hover,
.menu a.active {
  color: white;
}

/* Responsive */
@media (max-width: 1200px) {
  .menu {
    gap: 1rem;
  }

  .menu a {
    font-size: 0.75rem;
  }
}

@media (max-width: 768px) {
  .navbar {
    padding: 15px 20px;
  }

  .logo {
    font-size: 1.3rem;
  }
}

/* Mobile Menu Toggle */
.mobile-menu-toggle {
  display: none;
  flex-direction: column;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0.5rem;
  z-index: 1001;
}

.hamburger-line {
  width: 25px;
  height: 3px;
  background-color: white;
  margin: 3px 0;
  transition: all 0.3s ease;
  border-radius: 2px;
}

/* Mobile Menu Toggle Animation */
.mobile-menu-toggle.active .hamburger-line:nth-child(1) {
  transform: rotate(-45deg) translate(-5px, 6px);
}

.mobile-menu-toggle.active .hamburger-line:nth-child(2) {
  opacity: 0;
}

.mobile-menu-toggle.active .hamburger-line:nth-child(3) {
  transform: rotate(45deg) translate(-5px, -6px);
}

@media (max-width: 1024px) {
  .mobile-menu-toggle {
    display: flex;
  }

  .menu {
    position: fixed;
    top: 0;
    right: -100%;
    width: 100%;
    height: 100vh;
    background: rgba(19, 20, 24, 0.98);
    backdrop-filter: blur(10px);
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 2rem;
    transition: right 0.3s ease;
    z-index: 1000;
  }

  .menu.active {
    right: 0;
  }

  .menu a {
    font-size: 1.2rem;
    color: white;
    padding: 1rem;
  }
}

@media (max-width: 640px) {
  .navbar {
    padding: 15px 20px;
  }

  .logo-img {
    height: 3rem;
  }
}

/* About Section */
#about {
  padding-top: 1rem !important;
  padding-bottom: 2rem;
}

#about .section-header {
  margin-bottom: 1.5rem;
}

@media (max-width: 768px) {
  #about {
    padding-top: 2rem !important;
  }
}

.about-content {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-top: 1rem;
}

.about-text {
  padding: 2rem;
  background: rgba(255, 255, 255, 0.03);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  backdrop-filter: blur(10px);
}

.about-intro-title {
  font-size: 2rem;
  font-weight: 800;
