/* WhatsApp Button */
.whatsapp-button {
  position: fixed;
  bottom: 2rem;
  right: 2rem;
  z-index: 9999;
  display: flex;
  align-items: center;
  gap: 1rem;
  max-width: calc(100vw - 4rem);
}

.whatsapp-cta-text {
  background: transparent;
  border: 2px solid #25D366;
  padding: 0.75rem 1rem;
  border-radius: 25px;
  box-shadow: 0 4px 12px rgba(37, 211, 102, 0.3);
  opacity: 0;
  transform: translateX(20px);
  transition: all 0.5s ease;
  animation: bounce 2s infinite;
}

.whatsapp-cta-text.show {
  opacity: 1;
  transform: translateX(0);
}

.whatsapp-cta-text p {
  margin: 0;
  color: white;
  font-size: 0.875rem;
  font-weight: 500;
  white-space: nowrap;
}

.whatsapp-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 4.5rem;
  height: 4.5rem;
  background: transparent;
  border: none;
  color: white;
  text-decoration: none;
  transition: all 0.3s ease;
  animation: pulse 2s infinite;
}

.whatsapp-link:hover {
  transform: scale(1.1);
}

@keyframes bounce {

  0%,
  20%,
  50%,
  80%,
  100% {
    transform: translateY(0);
  }

  40% {
    transform: translateY(-5px);
  }

  60% {
    transform: translateY(-3px);
  }
}

/* Responsive WhatsApp CTA */
@media (max-width: 769px) {
  .whatsapp-button {
    bottom: 1rem;
    right: 1rem;
    flex-direction: column;
    align-items: flex-end;
    gap: 0.5rem;
  }

  .whatsapp-cta-text {
    order: -1;
    transform: translateY(20px);
    padding: 0.5rem 0.75rem;
    border-radius: 20px;
    max-width: 200px;
  }

  .whatsapp-cta-text.show {
    transform: translateY(0);
  }

  .whatsapp-cta-text p {
    font-size: 0.75rem;
    line-height: 1.3;
  }

  .whatsapp-link {
    width: 3rem;
    height: 3rem;
    flex-shrink: 0;
  }
}

@media (max-width: 480px) {
  .whatsapp-button {
    bottom: 0.75rem;
    right: 0.75rem;
  }

  .whatsapp-cta-text {
    padding: 0.4rem 0.6rem;
    border-radius: 16px;
    max-width: 180px;
  }

  .whatsapp-cta-text p {
    font-size: 0.7rem;
  }

  .whatsapp-link {
    width: 2.75rem;
    height: 2.75rem;
  }

  .whatsapp-link svg {
    width: 1.25rem;
    height: 1.25rem;
  }
}

@media (max-width: 360px) {
  .whatsapp-button {
    bottom: 0.5rem;
    right: 0.5rem;
    max-width: calc(100vw - 1rem);
  }

  .whatsapp-cta-text {
    padding: 0.35rem 0.5rem;
    border-radius: 14px;
    max-width: 160px;
  }

  .whatsapp-cta-text p {
    font-size: 0.65rem;
    line-height: 1.2;
  }

  .whatsapp-link {
    width: 2.5rem;
    height: 2.5rem;
  }

  .whatsapp-link svg {
    width: 1.1rem;
    height: 1.1rem;
  }
}

/* Landscape mobile optimization */
@media (max-width: 769px) and (orientation: landscape) {
  .whatsapp-button {
    bottom: 0.75rem;
    right: 0.75rem;
  }

  .whatsapp-cta-text {
