}

.device-screen {
  background: linear-gradient(145deg, #0f1419, #1a1a2e);
  border-radius: 15px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: #00aaff;
  text-align: center;
  border: 1px solid rgba(0, 150, 255, 0.3);
}

.device-icon {
  margin-bottom: 15px;
  color: #00aaff;
}

.device-screen h3 {
  font-size: 1.5rem;
  font-weight: 700;
  margin: 10px 0 5px;
  color: #ffffff;
}

.device-screen p {
  font-size: 0.9rem;
  color: #00aaff;
  margin: 0;
}

.orbital-ring {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 600px;
  height: 600px;
  border: 2px solid rgba(0, 150, 255, 0.3);
  border-radius: 50%;
  animation: rotate 30s linear infinite;
}

.orbital-ring::before {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  border-radius: 50%;
  background: conic-gradient(from 0deg, transparent, rgba(0, 150, 255, 0.5), transparent);
  animation: rotate 20s linear infinite reverse;
  z-index: -1;
}

@keyframes rotate {
  from {
    transform: translate(-50%, -50%) rotate(0deg);
  }

  to {
    transform: translate(-50%, -50%) rotate(360deg);
  }
}

.service-orbit {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 600px;
  height: 600px;
  transform: translate(-50%, -50%);
}

.service-node {
  position: absolute;
  width: 80px;
  height: 80px;
  background: linear-gradient(145deg, #1a1a2e, #16213e);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow:
    0 0 20px rgba(0, 150, 255, 0.4),
    inset 0 0 10px rgba(0, 150, 255, 0.1);
  border: 2px solid rgba(0, 150, 255, 0.6);
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 5;
}

.service-node:hover {
  transform: scale(1.25) rotate(5deg);
  box-shadow:
    0 0 40px rgba(0, 150, 255, 0.9),
    inset 0 0 20px rgba(0, 150, 255, 0.3);
  border-color: rgba(0, 150, 255, 0.8);
}

.service-node:active {
  transform: scale(1.1) rotate(2deg);
}

.service-node .service-icon {
  color: #00aaff;
  background: none;
  width: auto;
  height: auto;
  margin: 0;
  box-shadow: none;
}

.service-info {
  position: absolute;
  width: 320px;
  padding: 24px;
  background: linear-gradient(145deg, rgba(26, 26, 46, 0.95), rgba(22, 33, 62, 0.95));
  border-radius: 20px;
  border: 1px solid rgba(0, 150, 255, 0.3);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.6);
  backdrop-filter: blur(15px);
  opacity: 0;
  transform: scale(0.8) translateY(20px);
  transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 4;
  overflow: hidden;
}

.service-info::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, var(--accent-orange), var(--accent-blue));
  border-radius: 20px 20px 0 0;
}

.services-circular.visible .service-info {
  opacity: 1;
  transform: scale(1) translateY(0);
}

/* Floating particles effect */
.services-circular .floating-particle {
  position: absolute;
  width: 4px;
  height: 4px;
  background: rgba(0, 150, 255, 0.6);
  border-radius: 50%;
  pointer-events: none;
  animation: float 8s infinite ease-in-out;
}

.services-circular .floating-particle:nth-child(odd) {
  animation-delay: -2s;
  background: rgba(170, 92, 47, 0.6);
}

.services-circular .floating-particle:nth-child(3n) {
  animation-delay: -4s;
  animation-duration: 12s;
}

@keyframes float {

  0%,
  100% {
    transform: translateY(0) translateX(0) scale(1);
    opacity: 0;
  }

  10% {
    opacity: 1;
  }

  50% {
    transform: translateY(-100px) translateX(20px) scale(1.2);
    opacity: 0.8;
  }

  90% {
    opacity: 1;
  }
}

