#services .section-header .section-subtitle,
#image-carousel .section-header .section-title,
#image-carousel .section-header .section-subtitle,
#testimonials .section-header .section-title,
#testimonials .section-header .section-subtitle {
  transform: translateX(-100%);
  opacity: 0;
  transition: all 1.5s cubic-bezier(0.4, 0, 0.2, 1);
}

#services .section-header .section-title.animate-in,
#services .section-header .section-subtitle.animate-in,
#image-carousel .section-header .section-title.animate-in,
#image-carousel .section-header .section-subtitle.animate-in,
#testimonials .section-header .section-title.animate-in,
#testimonials .section-header .section-subtitle.animate-in {
  transform: translateX(0);
  opacity: 1;
}

#services .section-header .section-title.animate-in {
  transition-delay: 0.2s;
}

#services .section-header .section-subtitle.animate-in {
  transition-delay: 0.6s;
}

#image-carousel .section-header .section-title.animate-in {
  transition-delay: 0.2s;
}

#image-carousel .section-header .section-subtitle.animate-in {
  transition-delay: 0.6s;
}

#testimonials .section-header .section-title.animate-in {
  transition-delay: 0.2s;
}

#testimonials .section-header .section-subtitle.animate-in {
  transition-delay: 0.6s;
}

.mission-card:hover {
  background: rgba(255, 255, 255, 0.05);
  transform: translateY(-5px);
  box-shadow: 0 16px 40px rgba(0, 0, 0, 0.3);
}

.card-number {
  position: absolute;
  top: -15px;
  left: 2rem;
  width: 40px;
  height: 40px;
  background: #ffffff;
  color: #2a2a2a;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 18px;
  font-family: 'Montserrat', sans-serif;
}

.card-content {
  margin-top: 1rem;
}

.card-content h3 {
  color: #ffffff;
  font-size: 16px;
  font-weight: 700;
  letter-spacing: 1px;
  margin-bottom: 1.5rem;
  font-family: 'Montserrat', sans-serif;
}

.card-content p {
  color: #ffffff;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  margin: 0;
  font-family: 'Montserrat', sans-serif;
  letter-spacing: 0.5px;
}

/* Responsive design for mission cards */
@media (max-width: 1024px) {
  .mission-vision-cards {
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
  }

  .mission-card {
    height: 280px;
    padding: 2rem 1.5rem 1.5rem;
  }

  .card-content h3 {
    font-size: 15px;
  }

  .card-content p {
    font-size: 15px;
  }
}

@media (max-width: 768px) {
  .mission-vision-cards {
    gap: 1rem;
    margin-top: 3rem;
  }

  .mission-card {
    height: 240px;
    padding: 1.5rem 1rem;
  }

  .card-number {
    width: 35px;
    height: 35px;
    font-size: 16px;
  }

  .card-content h3 {
    font-size: 14px;
    margin-bottom: 1rem;
  }

  .card-content p {
    font-size: 14px;
  }
}


/* New Services Cards - Following exact image design */
.services-cards-new {
  display: flex;
  justify-content: center;
  align-items: flex-start;
  gap: 1.5rem;
  margin: 4rem auto 0;
  padding: 0 1rem;
  max-width: 1400px;
  width: 100%;
}

.service-card-new {
  background: transparent !important;
  border-radius: 16px;
  padding: 2rem 1.5rem;
  position: relative;
  height: 480px;
  width: 300px;
  flex: 0 0 300px;
  border: 2px solid;
  border-image-slice: 1;
  display: flex;
  flex-direction: column;
}

.service-card-new.blue-gradient {
  border: 2px solid #2c6ecb;
}

.service-card-new.green-gradient {
  border: 2px solid #7cc1df;
}

.service-card-new.brown-gradient {
  border: 2px solid #96b4a8;
}

.service-card-new.orange-gradient {
  border: 2px solid #aa5c2f;
}

.service-icon-new {
  position: absolute;
  top: 1.5rem;
  right: 1.5rem;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #ffffff;
  opacity: 0.8;
}

.service-title-new {
  color: #ffffff;
  font-size: 18px;
  font-weight: 700;
  letter-spacing: 1px;
  margin-bottom: 1rem;
  margin-top: 0;
  font-family: 'Montserrat', sans-serif;
  line-height: 1.3;
  max-width: 85%;
}

.service-divider-new {
  width: 60px;
  height: 2px;
  margin: 1.5rem 0;
}

.service-card-new.blue-gradient .service-divider-new {
  background: #2c6ecb;
}

.service-card-new.green-gradient .service-divider-new {
  background: #7cc1df;
}

.service-card-new.brown-gradient .service-divider-new {
  background: #96b4a8;
}

.service-card-new.orange-gradient .service-divider-new {
  background: #aa5c2f;
}

.service-description-new {
  color: #ffffff;
  font-size: 15px;
  font-weight: 400;
  line-height: 1.5;
  margin: 0;
  font-family: 'Montserrat', sans-serif;
  opacity: 0.9;
  min-height: 120px;
}

.service-footer-new {
  margin-top: auto;
  padding-top: 1.5rem;
  border-top: 1px solid rgba(255, 255, 255, 0.2);
}

.service-btn-new {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  width: 100%;
  padding: 0.875rem 1.5rem;
  background: transparent;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 12px;
  color: white;
  font-size: 0.85rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  cursor: pointer;
  transition: all 0.3s ease;
}

/* Hover effects for each card color */
.service-card-new.blue-gradient .service-btn-new:hover {
  background: rgba(44, 110, 203, 0.1);
  border-color: #2C6ECB;
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(44, 110, 203, 0.3);
}

.service-card-new.green-gradient .service-btn-new:hover {
  background: rgba(124, 193, 223, 0.1);
  border-color: #7CC1DF;
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(124, 193, 223, 0.3);
}

.service-card-new.brown-gradient .service-btn-new:hover {
  background: rgba(150, 180, 168, 0.1);
  border-color: #96B4A8;
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(150, 180, 168, 0.3);
}

.service-card-new.orange-gradient .service-btn-new:hover {
  background: rgba(170, 92, 47, 0.1);
  border-color: #AA5C2F;
  transform: translateY(-2px);
  box-shadow: 0 8px 16px rgba(170, 92, 47, 0.3);
}

.service-btn-new:active {
  transform: translateY(0);
}

/* Responsive design for service cards */
@media (max-width: 1200px) {
  .services-cards-new {
    flex-wrap: wrap;
    justify-content: center;
  }

  .service-card-new {
    flex: 0 0 280px;
    width: 280px;
    height: 460px;
  }
}

@media (max-width: 768px) {
  .services-cards-new {
    flex-direction: column;
    align-items: center;
    gap: 1rem;
  }

  .service-card-new {
    flex: 0 0 280px;
    width: 320px;
    height: 440px;
    padding: 1.5rem;
  }

  .service-title-new {
    font-size: 16px;
    max-width: 80%;
  }

  .service-description-new {
    font-size: 14px;
  }

  .service-icon-new {
    top: 1rem;
    right: 1rem;
    width: 35px;
    height: 35px;
  }
}

/* Additional override to ensure transparent backgrounds for all service cards */
.service-card-new, 
.service-card-new.blue-gradient, 
.service-card-new.green-gradient, 
.service-card-new.brown-gradient, 
.service-card-new.orange-gradient {
  background: transparent !important;
  background-color: transparent !important;
}

.service-card-new::before,
.service-card-new::after {
  background: transparent !important;
  background-color: transparent !important;
}

/* Hero Buttons */
.hero-buttons {
  display: flex;
  gap: 1.5rem;
  justify-content: center;
  align-items: center;
  margin-top: 2.5rem;
  flex-wrap: wrap;
}

.btn-primary-gradient {
  background: transparent;
  border: none;
  color: white;
  padding: 0.875rem 2rem;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  min-width: 200px;
  text-align: center;
  font-family: 'Montserrat', sans-serif;
  animation: pulse-continuous 2s ease-in-out infinite;
  text-decoration: none;
  display: inline-block;
}

.btn-primary-gradient::before {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
  border-radius: 27px;
  z-index: -1;
}

.btn-primary-gradient:hover {
  transform: scale(1.05);
  box-shadow: 0 8px 25px rgba(170, 92, 47, 0.3);
}

.btn-primary-gradient:active {
  transform: scale(0.98);
}

.btn-secondary-outline {
  background: transparent;
  border: 2px solid white;
  color: white;
  padding: 0.875rem 2rem;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  min-width: 200px;
  text-align: center;
  font-family: 'Montserrat', sans-serif;
  text-decoration: none;
  display: inline-block;
}

.btn-secondary-outline:hover {
  background: rgba(255, 255, 255, 0.1);
  box-shadow: 0 4px 15px rgba(255, 255, 255, 0.2);
}

.btn-secondary-outline:active {
  transform: scale(0.98);
}

/* Pulse animation for primary button */
@keyframes pulse-continuous {
  0% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(170, 92, 47, 0.4);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 0 10px rgba(170, 92, 47, 0);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(170, 92, 47, 0);
  }
}

/* Logo hover effects */
.logo-main {
  transition: all 0.3s ease;
  cursor: pointer;
}

.logo-main:hover {
  transform: scale(1.1);
  filter: brightness(1.2) drop-shadow(0 0 15px rgba(255, 255, 255, 0.3));
}

.footer-logo {
  transition: all 0.3s ease;
  cursor: pointer;
}

.footer-logo:hover {
  transform: scale(1.05);
  filter: brightness(1.1) drop-shadow(0 0 10px rgba(255, 255, 255, 0.2));
}

/* Footer Social Media */
.footer-social {
  margin-top: 1.5rem;
  display: flex;
  gap: 1rem;
}

.social-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 2.5rem;
  height: 2.5rem;
  background: transparent;
  border: 2px solid white;
  border-radius: 12px;
  color: white;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(255, 255, 255, 0.2);
}

.social-link:hover {
  transform: scale(1.1);
  box-shadow: 0 6px 20px rgba(255, 255, 255, 0.4);
  background: rgba(255, 255, 255, 0.1);
}

.social-link:active {
  transform: scale(0.95);
}

.social-link i {
  width: 1.25rem;
  height: 1.25rem;
}

/* Image Carousel Section - Override all global styles */
section#image-carousel {
  margin-top: 8rem;
  position: relative;
  background: #000000 !important;
  z-index: 1;
  overflow: hidden;
}

section#image-carousel::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url('/assets/BG10_1758037534669.png');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  z-index: -1;
}

section#image-carousel::after {
  content: none;
}

@keyframes gradient-flow {
  0% {
    background-position: 20% 30%, 80% 70%, 60% 10%, 30% 90%;
  }
  25% {
    background-position: 25% 35%, 75% 65%, 65% 15%, 25% 85%;
  }
  50% {
    background-position: 30% 40%, 70% 60%, 70% 20%, 20% 80%;
  }
  75% {
    background-position: 25% 35%, 75% 65%, 65% 15%, 25% 85%;
  }
  100% {
    background-position: 20% 30%, 80% 70%, 60% 10%, 30% 90%;
  }
}


@media (max-width: 768px) {
  section#image-carousel {
    margin-top: 6rem;
  }
}

.image-carousel-container {
  position: relative;
  max-width: 1400px;
  margin: 4rem auto;
  overflow: hidden;
  border-radius: 1.5rem;
  box-shadow: 8px 8px 16px rgba(0, 0, 0, 0.4), -8px -8px 16px rgba(255, 255, 255, 0.05);
  z-index: 2;
  height: 700px;
}

.image-carousel-slide {
  display: none;
  width: 100%;
  height: 700px;
}

.image-carousel-slide.active {
  display: block;
}

.image-carousel-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  align-items: center;
  gap: 4rem;
  padding: 4rem;
  height: 700px;
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.image-carousel-content:hover {
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.15);
  box-shadow: 0 16px 40px rgba(0, 0, 0, 0.25);
}

.image-carousel-text {
  z-index: 2;
}

.image-carousel-title {
  font-family: 'Montserrat', sans-serif;
  font-size: 3.2rem;
  font-weight: 800;
  color: white;
  margin-bottom: 2rem;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
  line-height: 1.1;
}

.image-carousel-subtitle {
  font-size: 1.4rem;
  line-height: 1.7;
  color: rgba(255, 255, 255, 0.95);
  margin-bottom: 0;
  font-weight: 400;
}

.image-carousel-image {
  position: relative;
  border-radius: 1rem;
  overflow: hidden;
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.4);
  aspect-ratio: 16/9;
}

.image-carousel-image img,
.image-carousel-image video {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
  border-radius: 1rem;
}

.image-carousel-nav {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  margin-top: 2rem;
}

.image-carousel-dots {
  display: flex;
  gap: 0.75rem;
}

.image-carousel-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  cursor: pointer;
  transition: all 0.3s ease;
}

.image-carousel-dot.active {
  background: linear-gradient(135deg, #AA5C2F, #96B4A8, #7CC1DF, #2C6ECB);
  transform: scale(1.2);
}

.image-carousel-arrows {
  display: flex;
  gap: 1rem;
}

.image-carousel-arrow {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 3rem;
  height: 3rem;
  background: transparent;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
}

.image-carousel-arrow:hover {
  background: rgba(255, 255, 255, 0.1);
  border-color: rgba(255, 255, 255, 0.6);
  transform: scale(1.1);
}

.image-carousel-arrow:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Responsive Design for Image Carousel */
@media (max-width: 768px) {
  .image-carousel-container {
    height: 900px;
  }

  .image-carousel-slide {
    height: 900px;
  }

  .image-carousel-content {
    grid-template-columns: 1fr;
    gap: 2.5rem;
    padding: 3rem;
    text-align: center;
    height: 900px;
    border-radius: 16px;
  }

  .image-carousel-image {
    height: 300px;
  }

  .image-carousel-title {
    font-size: 2.8rem;
  }

  .image-carousel-subtitle {
    font-size: 1.2rem;
  }

  .image-carousel-nav {
    flex-direction: column;
    gap: 1.5rem;
  }
}

@media (max-width: 480px) {
  .image-carousel-container {
    height: 850px;
  }

  .image-carousel-slide {
    height: 850px;
  }

  .image-carousel-content {
    padding: 2rem;
    height: 850px;
    border-radius: 12px;
  }

  .image-carousel-image {
    height: 250px;
  }

  .image-carousel-title {
    font-size: 2.2rem;
  }

  .image-carousel-subtitle {
    font-size: 1.1rem;
  }
}

/* Responsive buttons */
@media (max-width: 768px) {
  .hero-buttons {
    flex-direction: column;
    gap: 1rem;
  }

  .btn-primary-gradient,
  .btn-secondary-outline {
    width: 100%;
    max-width: 300px;
  }
}


/* Image Background */
.image-background {
  position: relative;
  background-image: url('/assets/BG10_1758037534669.png') !important;
  background-size: cover !important;
  background-position: center !important;
  background-repeat: no-repeat !important;
  background-attachment: scroll !important;
  min-height: 100vh !important;
}

/* Ensure content appears above the background image */
.image-background .container {
  position: relative;
  z-index: 2;
}

/* ===========================
   SECTION DIVIDERS
   =========================== */
.section-divider {
  position: relative;
  width: 100%;
  height: 1px;
  margin: 4rem 0;
  overflow: hidden;
}

.section-divider::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 1px;
  background: linear-gradient(
    90deg,
    transparent 0%,
    transparent 20%,
    rgba(255, 255, 255, 0.3) 50%,
    transparent 80%,
    transparent 100%
  );
}

/* Section dividers placed between sections */
.section-divider-line {
  position: relative;
  width: 100%;
  height: 1px;
  margin: 3rem 0;
  background: linear-gradient(
    90deg,
    transparent 0%,
    transparent 20%,
    rgba(255, 255, 255, 0.3) 50%,
    transparent 80%,
    transparent 100%
  );
  z-index: 10;
}

.section-divider-line::before {
  content: '';
  position: absolute;
  top: -2rem;
  left: 0;
  width: 100%;
  height: calc(1px + 4rem);
  background: rgba(11, 13, 16, 0.8);
  z-index: -1;
}

/* About CTA Button */
.about-cta {
  display: flex;
  justify-content: center;
  margin: 4rem 0;
}

.about-button {
  background: transparent;
  color: white;
  border: 2px solid rgba(255, 255, 255, 0.6);
  padding: 1.2rem 4rem;
  font-size: 1rem;
  font-weight: 600;
  border-radius: 12px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  min-width: 200px;
}

.about-button:hover {
  background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
  border-color: transparent;
  color: white;
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(170, 92, 47, 0.4);
}

.about-button i {
  width: 18px;
  height: 18px;
  transition: transform 0.3s ease;
}

.about-button:hover i {
  transform: translateX(4px);
}

/* Carousel Checkpoints */
.carousel-checkpoints {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-top: 1rem;
}

.checkpoint-item {
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
}

.checkpoint-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  flex-shrink: 0;
  margin-top: 0.25rem;
}

.checkpoint-text {
  color: rgba(255, 255, 255, 0.9);
  font-size: 0.9rem;
  line-height: 1.4;
  margin: 0;
  font-family: 'Montserrat', sans-serif;
}

public/css/article.css.content:
/* Article Page Styles */

.article-page {
  background: #0B0D10;
  min-height: 100vh;
  color: #FFFFFF;
  position: relative;
  overflow-x: hidden;
}

.article-page::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background:
    radial-gradient(circle at 20% 80%, rgba(59, 124, 255, 0.03) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(242, 106, 48, 0.03) 0%, transparent 50%);
  pointer-events: none;
  z-index: 0;
}

/* Hero Section */
.article-hero {
  background: linear-gradient(135deg, #0B0D10 0%, #1A1C20 100%);
  padding: 140px 0 40px;
  position: relative;
  z-index: 1;
  text-align: center;
}

.article-hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(ellipse at center, rgba(59, 124, 255, 0.08) 0%, transparent 70%);
  pointer-events: none;
}

.article-header {
  max-width: 900px;
  margin: 0 auto;
  text-align: center;
  position: relative;
  z-index: 2;
}

.breadcrumb {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  margin-bottom: 32px;
  font-size: 14px;
  color: #8B9095;
}

.breadcrumb a {
  color: #3B7CFF;
  text-decoration: none;
  transition: color 0.3s ease;
}

.breadcrumb a:hover {
  color: #5A94FF;
}

.breadcrumb i {
  width: 16px;
  height: 16px;
}

.article-meta {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 24px;
  margin-bottom: 24px;
  flex-wrap: wrap;
}

.meta-item {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 14px;
  color: #8B9095;
}

.meta-item i {
  width: 16px;
  height: 16px;
}

.article-title {
  font-size: 3rem;
  font-weight: 800;
  line-height: 1.1;
  margin-bottom: 24px;
  color: #FFFFFF;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
}

.article-subtitle {
  font-size: 1.2rem;
  line-height: 1.6;
  color: #B0B7BD;
  max-width: 700px;
  margin: 0 auto;
  font-weight: 400;
}

.article-category {
  margin-bottom: 3rem;
  sha font-size: 12px;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  padding: 6px 16px;
  border-radius: 6px;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.article-category.orange {
  color: #F26A30;
  background: rgba(242, 106, 48, 0.1);
  border-color: rgba(242, 106, 48, 0.3);
}

.article-title {
  font-size: 3.2rem;
  font-weight: 800;
  line-height: 1.1;
  margin-bottom: 32px;
  background: linear-gradient(135deg, #FFFFFF 0%, #E0E4E8 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.02em;
}

.article-meta {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 32px;
  margin-bottom: 32px;
  font-size: 14px;
  color: #8B9095;
}

.meta-item {
  display: flex;
  align-items: center;
  gap: 8px;
}

.meta-item i {
  width: 16px;
  height: 16px;
  color: #3B7CFF;
}

.article-subtitle {
  text-align: center;
  font-size: 1.3rem;
  line-height: 1.6;
  color: #B0B3B8;
  font-weight: 400;
  margin: 0 auto;
}

/* Article Content */
.article-content {
  padding: 5rem 0 120px;
  position: relative;
  z-index: 1;
}

.article-layout {
  display: block;
  max-width: 900px;
  margin: 0 auto;
}



/* Article Body */
.article-body {
  background: linear-gradient(145deg, rgba(42, 60, 78, 0.3) 0%, rgba(29, 31, 34, 0.5) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 24px;
  padding: 48px;
  box-shadow:
    0 16px 40px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.article-image {
  margin: 0 0 48px 0;
  border-radius: 16px;
  overflow: hidden;
}

.image-placeholder {
  background: linear-gradient(135deg, #2A3C4E 0%, #1D1F22 100%);
  border: 1px solid rgba(255, 255, 255, 0.1);
  padding: 80px 40px;
  text-align: center;
  color: #8B9095;
}

.placeholder-icon {
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 20px;
}

.placeholder-icon i {
  width: 40px;
  height: 40px;
  color: #FFFFFF;
}

.professional-image {
  width: 100%;
  height: auto;
  border-radius: 16px;
  overflow: hidden;
  background: linear-gradient(135deg, #1A1C20 0%, #2A3C4E 100%);
}

.professional-image svg {
  width: 100%;
  height: 400px;
  display: block;
}

.professional-article-image {
  width: 100%;
  height: 400px;
  object-fit: cover;
  border-radius: 16px;
  display: block;
}

.article-text h2 {
  font-size: 2rem;
  font-weight: 700;
  color: #FFFFFF;
  margin: 48px 0 24px 0;
  line-height: 1.3;
  letter-spacing: -0.01em;
}

.article-text h2:first-child {
  margin-top: 0;
}

.article-text h3 {
  font-size: 1.5rem;
  font-weight: 600;
  color: #FFFFFF;
  margin: 32px 0 16px 0;
  line-height: 1.4;
}

.article-text h4 {
  font-size: 1.2rem;
  font-weight: 600;
  color: #FFFFFF;
  margin: 24px 0 12px 0;
  line-height: 1.4;
}

.article-text p {
  font-size: 1.1rem;
  line-height: 1.8;
  color: #C1C4C8;
  margin-bottom: 24px;
  font-weight: 400;
}

.article-text ul,
.article-text ol {
  margin: 24px 0;
  padding-left: 24px;
}

.article-text li {
  font-size: 1.1rem;
  line-height: 1.7;
  color: #C1C4C8;
  margin-bottom: 8px;
}

.article-text strong {
  color: #FFFFFF;
  font-weight: 600;
}

/* Highlight Box */
.highlight-box {
  background: linear-gradient(135deg, rgba(59, 124, 255, 0.1) 0%, rgba(89, 196, 182, 0.1) 100%);
  border: 1px solid rgba(59, 124, 255, 0.3);
  border-radius: 16px;
  padding: 32px;
  margin: 32px 0;
}

.highlight-box h3 {
  color: #3B7CFF;
  margin-top: 0;
  margin-bottom: 20px;
}

.highlight-box ul {
  margin: 0;
  padding-left: 0;
  list-style: none;
}

.highlight-box li {
  position: relative;
  padding-left: 24px;
  margin-bottom: 12px;
  color: #FFFFFF;
}

.highlight-box li::before {
  content: '→';
  position: absolute;
  left: 0;
  color: #3B7CFF;
  font-weight: bold;
}

/* Quote Box */
.quote-box {
  background: linear-gradient(135deg, rgba(242, 106, 48, 0.1) 0%, rgba(255, 147, 77, 0.1) 100%);
  border-left: 4px solid #F26A30;
  border-radius: 0 16px 16px 0;
  padding: 32px;
  margin: 40px 0;
  position: relative;
}

.quote-box::before {
  content: '"';
  position: absolute;
  top: -10px;
  left: 20px;
  font-size: 4rem;
  color: #F26A30;
  opacity: 0.3;
  font-family: serif;
}

.quote-box blockquote {
  font-size: 1.2rem;
  line-height: 1.6;
  color: #FFFFFF;
  margin: 0 0 16px 0;
  font-style: italic;
  font-weight: 500;
}

.quote-box cite {
  font-size: 14px;
  color: #B0B3B8;
  font-style: normal;
}

/* Steps Container */
.steps-container {
  margin: 40px 0;
}

.step {
  display: flex;
  gap: 20px;
  margin-bottom: 32px;
  padding: 24px;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.step:hover {
  background: rgba(255, 255, 255, 0.05);
  border-color: rgba(255, 255, 255, 0.2);
}

.step-number {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #3B7CFF 0%, #5A94FF 100%);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #FFFFFF;
  font-weight: 700;
  font-size: 18px;
  flex-shrink: 0;
}

.step-content h4 {
  margin: 0 0 8px 0;
  color: #FFFFFF;
  font-size: 1.1rem;
}

.step-content p {
  margin: 0;
  color: #C1C4C8;
  font-size: 1rem;
  line-height: 1.6;
}

/* Metrics Grid */
.metrics-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 24px;
  margin: 40px 0;
}

.metric-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.05) 0%, rgba(255, 255, 255, 0.02) 100%);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 24px;
  text-align: center;
  transition: all 0.3s ease;
}

.metric-card:hover {
  transform: translateY(-4px);
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.08) 0%, rgba(255, 255, 255, 0.04) 100%);
  border-color: rgba(255, 255, 255, 0.2);
}

.metric-card h4 {
  margin: 0 0 8px 0;
  color: #FFFFFF;
  font-size: 1.1rem;
}

.metric-card p {
  margin: 0 0 16px 0;
  color: #8B9095;
  font-size: 14px;
}

.metric-value {
  font-size: 2rem;
  font-weight: 700;
  background: linear-gradient(135deg, #3B7CFF 0%, #59C4B6 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* CTA Section */
.cta-section {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
  border-radius: 20px;
  padding: 48px;
  text-align: center;
  margin: 48px 0;
  color: #FFFFFF;
}

.cta-section h3 {
  margin: 0 0 16px 0;
  color: #FFFFFF;
  font-size: 1.8rem;
}

.cta-section p {
  margin: 0 0 32px 0;
  color: rgba(255, 255, 255, 0.9);
  font-size: 1.1rem;
}

.cta-button {
  display: inline-flex;
  align-items: center;
  gap: 12px;
  padding: 16px 32px;
  background: #FFFFFF;
  color: #F26A30;
  text-decoration: none;
  border-radius: 12px;
  font-weight: 700;
  font-size: 1.1rem;
  transition: all 0.3s ease;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
}

.cta-button:hover {
  background: rgba(255, 255, 255, 0.95);
  transform: translateY(-2px);
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.3);
}

/* Article Tags */
.article-tags {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
  margin-top: 48px;
  padding-top: 32px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.tag {
  padding: 8px 16px;
  background: rgba(255, 255, 255, 0.1);
  color: #B0B3B8;
  border-radius: 20px;
  font-size: 14px;
  font-weight: 500;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s ease;
}

.tag:hover {
  background: rgba(59, 124, 255, 0.2);
  color: #FFFFFF;
  border-color: #3B7CFF;
}

/* Sidebar */
.article-sidebar {
  position: static;
  height: fit-content;
  align-self: start;
}

.sidebar-widget {
  background: linear-gradient(145deg, rgba(42, 60, 78, 0.4) 0%, rgba(29, 31, 34, 0.6) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 32px;
  margin-bottom: 32px;
  margin-top: 0;
  box-shadow:
    0 8px 32px rgba(0, 0, 0, 0.3),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.sidebar-widget:first-child {
  margin-top: 0;
}

.sidebar-widget h3 {
  font-size: 1.2rem;
  font-weight: 600;
  margin: 0 0 24px 0;
  color: #FFFFFF;
}

/* Related Posts */
.related-posts {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.related-post {
  display: flex;
  gap: 16px;
  padding: 16px;
  border-radius: 12px;
  transition: background 0.3s ease;
}

.related-post:hover {
  background: rgba(255, 255, 255, 0.05);
}

.post-thumbnail {
  flex-shrink: 0;
}

.post-icon {
  width: 48px;
  height: 48px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #FFFFFF;
}

.post-icon.blue {
  background: linear-gradient(135deg, #3AAFF4 0%, #3B7CFF 100%);
}

.post-icon.teal {
  background: linear-gradient(135deg, #59C4B6 0%, #61A8A1 100%);
}

.post-icon.purple {
  background: linear-gradient(135deg, #5A6C89 0%, #2C3645 100%);
}

.post-icon.green {
  background: linear-gradient(135deg, #96B4A8 0%, #7A9B8A 100%);
}

.post-icon i {
  width: 20px;
  height: 20px;
}

.post-content h4 {
  margin: 0 0 8px 0;
  font-size: 15px;
  font-weight: 500;
  line-height: 1.3;
}

.post-content a {
  color: #FFFFFF;
  text-decoration: none;
  transition: color 0.3s ease;
}

.post-content a:hover {
  color: #3B7CFF;
}

.post-date {
  font-size: 13px;
  color: #8B9095;
}

/* Share Widget */
.share-widget .share-buttons {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.share-btn {
  margin: 10px;
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  border-radius: 8px;
  text-decoration: none;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.3s ease;
}

.share-btn.linkedin {
  background: rgba(0, 119, 181, 0.2);
  color: #0077B5;
  border: 1px solid rgba(0, 119, 181, 0.3);
}

.share-btn.linkedin:hover {
  background: rgba(0, 119, 181, 0.3);
  color: #FFFFFF;
}

.share-btn.twitter {
  background: rgba(29, 161, 242, 0.2);
  color: #1DA1F2;
  border: 1px solid rgba(29, 161, 242, 0.3);
}

.share-btn.twitter:hover {
  background: rgba(29, 161, 242, 0.3);
  color: #FFFFFF;
}

.share-btn.facebook {
  background: rgba(24, 119, 242, 0.2);
  color: #1877F2;
  border: 1px solid rgba(24, 119, 242, 0.3);
}

.share-btn.facebook:hover {
  background: rgba(24, 119, 242, 0.3);
  color: #FFFFFF;
}

.share-btn.whatsapp {
  background: rgba(37, 211, 102, 0.2);
  color: #25D366;
  border: 1px solid rgba(37, 211, 102, 0.3);
}

.share-btn.whatsapp:hover {
  background: rgba(37, 211, 102, 0.3);
  color: #FFFFFF;
}

/* CTA Widget */
.cta-widget {
  background: linear-gradient(135deg, #F26A30 0%, #FF934D 100%);
  border: none;
  text-align: center;
}

.cta-widget h3 {
  color: #FFFFFF;
  margin-bottom: 12px;
}

.cta-widget p {
  color: rgba(255, 255, 255, 0.9);
  font-size: 14px;
  margin-bottom: 24px;
  line-height: 1.5;
}

.cta-widget .cta-button {
  padding: 12px 24px;
  font-size: 14px;
}

/* Responsive */
@media (min-width: 1201px) {
  .article-layout {
    grid-template-columns: 1fr 320px;
    gap: 80px;
  }
}

@media (max-width: 1200px) and (min-width: 969px) {
  .article-layout {
    grid-template-columns: 1fr 280px;
    gap: 60px;
  }
}

@media (max-width: 968px) {
  .article-layout {
    grid-template-columns: 1fr;
    gap: 40px;
  }

  .article-sidebar {
    position: static;
    order: 2;
  }

  .article-body {
    order: 1;
  }
}

@media (max-width: 768px) {
  .article-hero {
    padding: 100px 0 60px;
  }

  .article-title {
    font-size: 2.2rem;
  }

  .article-meta {
    flex-direction: column;
    gap: 16px;
  }

  .article-content {
    padding: 60px 0 100px;
  }

  .article-body {
    padding: 32px 24px;
  }

  .sidebar-widget {
    margin-bottom: 24px;
    padding: 24px;
  }

  .metrics-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (max-width: 480px) {
  .article-title {
    font-size: 1.8rem;
  }

  .article-body {
    padding: 24px 20px;
  }

  .article-text h2 {
    font-size: 1.6rem;
  }

  .metrics-grid {
    grid-template-columns: 1fr;
  }

  .steps-container .step {
    flex-direction: column;
    text-align: center;
  }
}

.footer-bottom {
  border-top: none;
  padding-top: 2rem;
  text-align: center;
  color: hsl(var(--muted-foreground));
}

