  showNotification(message, type = 'info') {
    // Create notification element
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.textContent = message;

    // Style the notification
    Object.assign(notification.style, {
      position: 'fixed',
      top: '20px',
      right: '20px',
      padding: '1rem 1.5rem',
      borderRadius: '0.5rem',
      color: 'white',
      fontWeight: '500',
      zIndex: '9999',
      transform: 'translateX(100%)',
      transition: 'transform 0.3s ease',
      maxWidth: '400px',
      wordWrap: 'break-word'
    });

    // Set background color based on type
    switch (type) {
      case 'success':
        notification.style.backgroundColor = '#10b981';
        break;
      case 'error':
        notification.style.backgroundColor = '#ef4444';
        break;
      default:
        notification.style.backgroundColor = '#3b82f6';
    }

    // Add to page
    document.body.appendChild(notification);

    // Animate in
    setTimeout(() => {
      notification.style.transform = 'translateX(0)';
    }, 100);

    // Remove after 5 seconds
    setTimeout(() => {
      notification.style.transform = 'translateX(100%)';
      setTimeout(() => {
        if (notification.parentNode) {
          notification.parentNode.removeChild(notification);
        }
      }, 300);
    }, 5000);
  }

  setupScrollEffects() {
    // Add scroll-based animations
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');

          // Animate impact numbers
          if (entry.target.classList.contains('impact-number')) {
            this.animateNumber(entry.target);
          }
        }
      });
    }, observerOptions);

    // Observe all animated elements
    const animatedElements = document.querySelectorAll('.fade-in, .slide-in-left, .slide-in-right, .impact-number');
    animatedElements.forEach(el => observer.observe(el));
  }

  animateNumber(element) {
    if (element.dataset.animated) return;
    element.dataset.animated = 'true';

    const text = element.textContent;
    const hasPercent = text.includes('%');
    const isNegative = text.includes('-');
    const isPositive = text.includes('+');

    const number = parseFloat(text.replace(/[^0-9]/g, ''));
    if (isNaN(number)) return;

    let current = 0;
    const increment = number / 60; // 60 frames

    const animate = () => {
      current += increment;
      if (current >= number) {
        current = number;
      }

      let display = Math.floor(current).toString();
      if (isPositive && current > 0) display = '+' + display;
      if (isNegative) display = '-' + display;
      if (hasPercent) display += '%';

      element.textContent = display;

      if (current < number) {
        requestAnimationFrame(animate);
      }
    };

    animate();
  }

  setupAnimations() {
    // Add stagger delays to elements
    const sections = document.querySelectorAll('section');
    sections.forEach(section => {
      const cards = section.querySelectorAll('.fade-in');
      cards.forEach((card, index) => {
        card.style.animationDelay = `${index * 0.1}s`;
      });
    });

    // Setup mission cards animation on scroll
    this.setupMissionCardsAnimation();

    // Setup services and projects headers animation
    this.setupSectionHeadersAnimation();

    // Respect reduced motion preference
    if (window.matchMedia('(prefers-reduced-motion: reduce)').matches) {
      document.documentElement.style.setProperty('--animation-duration', '0.01ms');
    }
  }

  setupMissionCardsAnimation() {
    const aboutSection = document.getElementById('about');
    const missionCards = document.querySelectorAll('.mission-card');

    if (!aboutSection || missionCards.length === 0) return;

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          // Add animation class to all mission cards
          missionCards.forEach(card => {
            card.classList.add('animate-in');
          });

          // Stop observing once animated
          observer.unobserve(entry.target);
        }
      });
    }, {
      threshold: 0.3, // Trigger when 30% of the section is visible
      rootMargin: '0px 0px -50px 0px' // Add some margin to trigger earlier
    });

    observer.observe(aboutSection);
  }

  setupSectionHeadersAnimation() {
    const servicesSection = document.getElementById('services');
    const projectsSection = document.getElementById('image-carousel');
    const testimonialsSection = document.getElementById('testimonials');

    const setupSectionAnimation = (section) => {
      if (!section) return;

      const sectionTitle = section.querySelector('.section-header .section-title');
      const sectionSubtitle = section.querySelector('.section-header .section-subtitle');

      if (!sectionTitle || !sectionSubtitle) return;

      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            // Add animation class to title and subtitle
            sectionTitle.classList.add('animate-in');
            sectionSubtitle.classList.add('animate-in');

            // Stop observing once animated
            observer.unobserve(entry.target);
          }
        });
      }, {
        threshold: 0.2, // Trigger when 20% of the section is visible
        rootMargin: '0px 0px -50px 0px' // Add some margin to trigger earlier
      });

      observer.observe(section);
    };

    // Setup animation for all sections
    setupSectionAnimation(servicesSection);
    setupSectionAnimation(projectsSection);
    setupSectionAnimation(testimonialsSection);
  }

  // Utility method to format phone numbers
  formatPhone(phone) {
    const cleaned = phone.replace(/\D/g, '');
    const match = cleaned.match(/^(\d{2})(\d{4,5})(\d{4})$/);
    if (match) {
      return `(${match[1]}) ${match[2]}-${match[3]}`;
    }
    return phone;
  }

  // Add loading states for async operations
  setLoading(element, isLoading) {
    if (isLoading) {
      element.classList.add('loading');
      element.disabled = true;
    } else {
      element.classList.remove('loading');
      element.disabled = false;
    }
  }

  // Setup Service Buttons to redirect to WhatsApp
  setupServiceButtons() {
    const serviceButtons = {
      'button-saiba-mais-ia': 'Olá! Gostaria de saber mais sobre Inteligência Artificial Aplicada.',
      'button-saiba-mais-automacao': 'Olá! Gostaria de saber mais sobre Automação Inteligente.',
      'button-saiba-mais-dashboard': 'Olá! Gostaria de saber mais sobre Dashboards Interativos.',
      'button-saiba-mais-software': 'Olá! Gostaria de saber mais sobre Desenvolvimento de Software Personalizado.'
    };

    Object.keys(serviceButtons).forEach(testId => {
      const button = document.querySelector(`[data-testid="${testId}"]`);
      if (button) {
        button.addEventListener('click', () => {
          const message = encodeURIComponent(serviceButtons[testId]);
          const whatsappUrl = `https://wa.me/5511912906767?text=${message}`;
          window.open(whatsappUrl, '_blank');
        });
      }
    });
  }

  // Setup WhatsApp Call-to-Action
  setupWhatsAppCTA() {
    const whatsappCTA = document.getElementById('whatsapp-cta');

    if (whatsappCTA) {
      // Show the CTA after 6 seconds
      setTimeout(() => {
        whatsappCTA.style.display = 'block';
        setTimeout(() => {
          whatsappCTA.classList.add('show');
        }, 100);
      }, 6000);

      // Hide CTA after user clicks on WhatsApp button
      const whatsappLink = document.querySelector('.whatsapp-link');
      if (whatsappLink) {
        whatsappLink.addEventListener('click', () => {
          whatsappCTA.classList.remove('show');
          setTimeout(() => {
            whatsappCTA.style.display = 'none';
          }, 500);
        });
      }

      // Auto-hide CTA after 10 seconds of showing
      setTimeout(() => {
        if (whatsappCTA.classList.contains('show')) {
          whatsappCTA.classList.remove('show');
          setTimeout(() => {
            whatsappCTA.style.display = 'none';
          }, 500);
        }
      }, 16000); // 6 seconds delay + 10 seconds showing
    }
  }
}

// Initialize the website
const website = new DuatxWebsite();

// Global functions for button clicks
window.scrollToSection = function(sectionId) {
  website.scrollToSection(sectionId);
};

// Global functions for cookie banner (called from HTML onclick events)
function acceptAllCookies() {
  const preferences = {
    essential: true,
    analytics: true,
    marketing: true
  };
  website.saveConsentPreferences(preferences);
}

function showCookiePreferences() {
  website.showCookieModal();
}

function showCookieModal(event) {
  if (event) event.preventDefault();
  website.showCookieModal();
}

function closeCookieModal() {
  website.hideCookieModal();
}

function savePreferences() {
  const analyticsCheckbox = document.getElementById('analytics-cookies');
  const marketingCheckbox = document.getElementById('marketing-cookies');

  const preferences = {
    essential: true, // Always true
    analytics: analyticsCheckbox ? analyticsCheckbox.checked : false,
    marketing: marketingCheckbox ? marketingCheckbox.checked : false
  };

  website.saveConsentPreferences(preferences);
}

function rejectOptionalCookies() {
  const preferences = {
    essential: true,
    analytics: false,
    marketing: false
  };
  website.saveConsentPreferences(preferences);
}

// Phone number formatting for contact form
document.addEventListener('DOMContentLoaded', () => {
  const phoneInput = document.getElementById('phone');
  if (phoneInput) {
    phoneInput.addEventListener('input', (e) => {
      let value = e.target.value.replace(/\D/g, '');
      if (value.length > 11) value = value.slice(0, 11);

      if (value.length >= 11) {
        value = value.replace(/^(\d{2})(\d{5})(\d{4})$/, '($1) $2-$3');
      } else if (value.length >= 7) {
        value = value.replace(/^(\d{2})(\d{4})(\d+)$/, '($1) $2-$3');
      } else if (value.length >= 3) {
        value = value.replace(/^(\d{2})(\d+)$/, '($1) $2');
      }

      e.target.value = value;
    });
  }
});

// Add CSS for notifications and loading states
const notificationStyles = `
.notification {
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  border-left: 4px solid rgba(255, 255, 255, 0.3);
}

.loading {
  position: relative;
  pointer-events: none;
  opacity: 0.6;
}

.loading::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 20px;
  height: 20px;
  margin: -10px 0 0 -10px;
  border: 2px solid hsl(var(--primary));
  border-top: 2px solid transparent;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.nav-link.active {
  color: hsl(var(--blue-400));
}

.nav-link.active::after {
  width: 100%;
}

@media (max-width: 768px) {
  .notification {
    right: 10px;
    left: 10px;
    max-width: none;
  }
}
`;

const styleElement = document.createElement('style');
styleElement.textContent = notificationStyles;
document.head.appendChild(styleElement);

// Modern Blog Carousel Extension for DuatxWebsite
DuatxWebsite.prototype.setupModernBlogCarousel = function() {
  const container = document.querySelector('.blog-carousel-modern');
  const prevBtn = document.getElementById('blog-exact-prev');
  const nextBtn = document.getElementById('blog-exact-next');
  const dotsContainer = document.getElementById('blog-exact-dots');

  if (!container || !prevBtn || !nextBtn || !dotsContainer) {
    console.warn('Modern blog carousel elements not found');
    return;
  }

  let currentIndex = 0;
  const cards = container.querySelectorAll('.blog-card-exact');
  const totalCards = cards.length;

  if (totalCards === 0) {
    console.warn('No modern blog cards found');
    return;
  }

  console.log(`Setting up modern blog carousel with ${totalCards} cards`);

  function calculateDimensions() {
    const cardWidth = window.innerWidth >= 1024 ? 300 : (window.innerWidth >= 768 ? 280 : 260);
    const gap = 24;
    const visibleCards = window.innerWidth >= 1024 ? 4 : (window.innerWidth >= 768 ? 2 : 1);
    return { cardWidth, gap, visibleCards };
  }

  function createDots() {
    const { visibleCards } = calculateDimensions();
    const maxSlides = Math.max(1, totalCards - visibleCards + 1);

    dotsContainer.innerHTML = '';
    for (let i = 0; i < maxSlides; i++) {
      const dot = document.createElement('div');
      dot.className = `dot-blog ${i === 0 ? 'active' : ''}`;
      dot.addEventListener('click', () => goToSlide(i));
      dotsContainer.appendChild(dot);
    }
  }

  function updateCarousel() {
    const { cardWidth, gap, visibleCards } = calculateDimensions();
    const maxIndex = Math.max(0, totalCards - visibleCards);

    currentIndex = Math.min(currentIndex, maxIndex);

    const translateX = currentIndex * (cardWidth + gap);
    container.style.transform = `translateX(-${translateX}px)`;

    // Update dots
    document.querySelectorAll('.dot-blog').forEach((dot, index) => {
      dot.classList.toggle('active', index === currentIndex);
    });

    // Update buttons
    prevBtn.disabled = currentIndex === 0;
    nextBtn.disabled = currentIndex >= maxIndex;
  }

  function goToSlide(index) {
    const { visibleCards } = calculateDimensions();
    const maxIndex = Math.max(0, totalCards - visibleCards);
    currentIndex = Math.max(0, Math.min(index, maxIndex));
    updateCarousel();
  }

  function nextSlide() {
    const { visibleCards } = calculateDimensions();
    const maxIndex = Math.max(0, totalCards - visibleCards);
    if (currentIndex < maxIndex) {
      currentIndex++;
      updateCarousel();
    }
  }

  function prevSlide() {
    if (currentIndex > 0) {
      currentIndex--;
      updateCarousel();
    }
  }

  // Event listeners
  prevBtn.addEventListener('click', prevSlide);
  nextBtn.addEventListener('click', nextSlide);

  // Initialize
  createDots();
  updateCarousel();

  // Handle resize
  window.addEventListener('resize', () => {
    createDots();
    updateCarousel();
  });

  console.log('Modern blog carousel initialized successfully');
};

// Final Blog Carousel Extension for DuatxWebsite
