#!/usr/bin/env node

// Simple build script for static site deployment
import fs from 'fs';
import path from 'path';

console.log('Building static site...');

// Ensure dist directory exists
if (!fs.existsSync('dist')) {
  fs.mkdirSync('dist', { recursive: true });
}

// Copy all files from public to dist
function copyDir(src, dest) {
  if (!fs.existsSync(dest)) {
    fs.mkdirSync(dest, { recursive: true });
  }

  const files = fs.readdirSync(src);

  for (const file of files) {
    const srcPath = path.join(src, file);
    const destPath = path.join(dest, file);

    if (fs.statSync(srcPath).isDirectory()) {
      copyDir(srcPath, destPath);
    } else {
      fs.copyFileSync(srcPath, destPath);
    }
  }
}

try {
  copyDir('public', 'dist');

  // Copy root index.html for Vite compatibility
  if (fs.existsSync('index.html')) {
    fs.copyFileSync('index.html', 'dist/index.html');
  }

  console.log('✓ Static site built successfully!');
  console.log('✓ Files copied from public/ to dist/');
  console.log('✓ Root index.html added for deployment compatibility');
  console.log('✓ Site ready for deployment at dist/');
} catch (error) {
  console.error('✗ Build failed:', error.message);
  process.exit(1);
}

public/css/projeto-saude.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Montserrat', sans-serif;
    background: #0B0D10;
    color: #FFFFFF;
    line-height: 1.6;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

/* Color Variables */
:root {
    --orange: #AA5C2F;
    --green: #96B4A8;
    --blue: #7CC1DF;
    --navy: #2C6ECB;
    --dark-bg: #0B0D10;
    --card-bg: #1A1D23;
    --text-light: #CFCFCD;
}

/* Header */
.project-header {
    background: rgba(11, 13, 16, 0.95);
    backdrop-filter: blur(20px);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 100;
    padding: 1rem 0;
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.back-link {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    color: #FFFFFF;
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
}

.back-link:hover {
    color: var(--orange);
    transform: translateX(-2px);
}

.contact-cta {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
    color: #FFFFFF;
    text-decoration: none;
    border-radius: 50px;
    font-weight: 600;
    transition: all 0.3s ease;
    box-shadow: 0 4px 20px rgba(170, 92, 47, 0.3);
}

.contact-cta:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 30px rgba(170, 92, 47, 0.4);
}

/* Main Content */
.project-main {
    padding-top: 80px;
}

/* Project Cover */
.project-cover {
    background: linear-gradient(135deg, #0B0D10 0%, #1A1D23 100%);
    padding: 4rem 0;
    position: relative;
    overflow: hidden;
}

.cover-content {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 2rem;
    display: grid;
    grid-template-columns: 1fr 2fr;
    gap: 4rem;
    align-items: center;
    min-height: 600px;
}

.cover-brand {
    position: absolute;
    top: 2rem;
    left: 2rem;
}

.cover-logo {
    width: 120px;
    height: auto;
    filter: brightness(0) invert(1);
}

.cover-text {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    padding-right: 3rem;
}

.cover-icon {
    width: 80px;
    height: 80px;
    background: linear-gradient(135deg, var(--blue), var(--navy));
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1rem;
}

.cover-icon i {
    width: 40px;
    height: 40px;
    color: #FFFFFF;
}

.cover-title {
    font-size: 2.8rem;
    font-weight: 400;
    line-height: 1.1;
    color: #FFFFFF;
    margin: 0;
}

.cover-title strong {
    font-weight: 800;
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.cover-dashboard {
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    perspective: 1500px;
    min-height: 500px;
}

.cover-dashboard-image-back {
    position: absolute;
    width: 100%;
    max-width: 650px;
    height: auto;
    border-radius: 20px;
    box-shadow: 0 40px 100px rgba(0, 0, 0, 0.5);
    transform: translateX(-150px) translateY(20px) rotateY(25deg) rotateX(5deg);
    z-index: 1;
    transition: all 0.4s ease;
}

.cover-dashboard-image {
    position: relative;
    width: 100%;
    max-width: 650px;
    height: auto;
    border-radius: 20px;
    box-shadow: 0 50px 120px rgba(0, 0, 0, 0.6);
    transform: translateX(50px) translateY(-20px) rotateY(-15deg) rotateX(5deg);
    z-index: 2;
    transition: all 0.4s ease;
}

.cover-dashboard:hover .cover-dashboard-image-back {
    transform: translateX(-160px) translateY(25px) rotateY(28deg) rotateX(6deg);
}

.cover-dashboard:hover .cover-dashboard-image {
    transform: translateX(60px) translateY(-25px) rotateY(-18deg) rotateX(6deg);
}

/* About Project */
.about-project {
    padding: 5rem 0;
    background: var(--card-bg);
    margin: 4rem 0;
    border-radius: 30px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.about-header {
    text-align: center;
    margin-bottom: 4rem;
}

.about-title {
    font-size: 2.5rem;
    font-weight: 900;
    color: #FFFFFF;
    letter-spacing: 2px;
    background: linear-gradient(135deg, var(--orange) 0%, var(--blue) 100%);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    margin: 0;
}

.about-content {
    max-width: 800px;
    margin: 0 auto;
    padding: 0 2rem;
}

.about-section {
    margin-bottom: 3rem;
    padding: 2rem;
    background: rgba(255, 255, 255, 0.03);
    border-radius: 20px;
    border-left: 4px solid var(--orange);
}

.about-section:nth-child(2) {
    border-left-color: var(--green);
}

.about-section:nth-child(3) {
    border-left-color: var(--blue);
}

.about-section:nth-child(4) {
    border-left-color: var(--navy);
}

.about-subtitle {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--orange);
    margin-bottom: 1rem;
}

.about-section:nth-child(2) .about-subtitle {
    color: var(--green);
}

.about-section:nth-child(3) .about-subtitle {
    color: var(--blue);
}

.about-section:nth-child(4) .about-subtitle {
    color: var(--navy);
}

.about-text {
    font-size: 1.1rem;
    line-height: 1.8;
    color: var(--text-light);
    margin-bottom: 2rem;
}

.about-text:last-child {
    margin-bottom: 0;
}

.about-text strong {
    color: #FFFFFF;
    font-weight: 600;
}

/* Project Hero */
.project-hero {
    padding: 4rem 0;
    text-align: center;
    background: radial-gradient(ellipse at center, rgba(44, 110, 203, 0.05) 0%, transparent 70%);
}

.project-category {
    display: inline-block;
    padding: 0.5rem 1rem;
    background: rgba(170, 92, 47, 0.1);
    color: var(--orange);
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 600;
    letter-spacing: 0.05em;
    margin-bottom: 1.5rem;
}

.project-title {
    font-size: 3.5rem;
    font-weight: 800;
    line-height: 1.2;
    margin-bottom: 1.5rem;
}

.gradient-text {
    background: linear-gradient(135deg, var(--orange) 0%, var(--blue) 25%, var(--green) 50%, var(--navy) 100%);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    display: block;
    margin-top: 0.5rem;
}

.project-description {
    font-size: 1.25rem;
    color: var(--text-light);
    max-width: 800px;
    margin: 0 auto;
    line-height: 1.8;
}

/* Project Gallery */
.project-gallery {
    padding: 4rem 0;
}

.project-carousel-container {
    position: relative;
    max-width: 1200px;
    margin: 0 auto;
    text-align: center;
}

.project-carousel {
    overflow: hidden;
    border-radius: 20px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
    background: #0B0D10;
    margin-bottom: 2rem;
}

.carousel-track {
    display: flex;
    transition: transform 0.5s ease-in-out;
}

.carousel-slide {
    min-width: 100%;
    position: relative;
    display: flex;
    flex-direction: column;
    background: #0B0D10;
    gap: 0;
}

.carousel-slide.active {
    display: flex;
}

.project-image {
    width: 100%;
    height: 500px;
    display: block;
    object-fit: contain;
    object-position: center bottom;
    background: #0B0D10;
    margin: 0;
    padding: 0;
}

.image-caption {
    background: linear-gradient(135deg, var(--card-bg) 0%, rgba(26, 29, 35, 0.95) 100%);
    padding: 2rem;
    margin: 0;
    text-align: center;
    width: 100%;
    box-sizing: border-box;
}

.image-caption h3 {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
    color: #FFFFFF;
    background: linear-gradient(135deg, var(--orange) 0%, var(--blue) 100%);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
}

.image-caption p {
    color: var(--text-light);
    font-size: 1.1rem;
    line-height: 1.6;
}

/* Carousel Controls */
.carousel-controls {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 100%;
    display: flex;
    justify-content: space-between;
    padding: 0 1rem;
    pointer-events: none;
}

.carousel-btn {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    background: rgba(26, 29, 35, 0.9);
    border: 2px solid rgba(255, 255, 255, 0.2);
    color: #FFFFFF;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    pointer-events: all;
    backdrop-filter: blur(10px);
}

.carousel-btn:hover {
    background: var(--orange);
    border-color: var(--orange);
    transform: scale(1.1);
}

.carousel-btn i {
    width: 24px;
    height: 24px;
}

/* Carousel Dots */
.carousel-dots {
    display: flex;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 2rem;
    flex-wrap: wrap;
}

.dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    border: none;
    cursor: pointer;
    transition: all 0.3s ease;
}

.dot.active {
    background: var(--orange);
    transform: scale(1.2);
}

.dot:hover {
    background: var(--blue);
}

/* Project Features */
.project-features {
    margin-bottom: 4rem;
}

.feature-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
    margin-top: 2rem;
}

.feature-card {
    background: var(--card-bg);
    padding: 2rem;
    border-radius: 20px;
    text-align: center;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
}

.feature-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.feature-icon {
    width: 80px;
    height: 80px;
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1.5rem;
}

.orange-gradient { background: linear-gradient(135deg, var(--orange), #D17A4A); }
.green-gradient { background: linear-gradient(135deg, var(--green), #B5CCC1); }
.blue-gradient { background: linear-gradient(135deg, var(--blue), #9DD1E5); }
.navy-gradient { background: linear-gradient(135deg, var(--navy), #4A85D6); }

.feature-icon i {
    width: 32px;
    height: 32px;
    color: #FFFFFF;
}

.feature-card h4 {
    font-size: 1.25rem;
    font-weight: 700;
    margin-bottom: 1rem;
    color: #FFFFFF;
}

.feature-card p {
    color: var(--text-light);
}

/* Project Metrics */
.project-metrics {
    background: var(--card-bg);
    border-radius: 30px;
    padding: 3rem;
    margin-bottom: 4rem;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.metrics-header {
    text-align: center;
    margin-bottom: 3rem;
}

.metrics-header h3 {
    font-size: 2rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
}

.metrics-header p {
    color: var(--text-light);
    font-size: 1.1rem;
}

.metrics-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
}

.metric-card {
    text-align: center;
    padding: 1.5rem;
    border-radius: 15px;
    background: rgba(255, 255, 255, 0.03);
}

.metric-value {
    font-size: 3rem;
    font-weight: 900;
    margin-bottom: 0.5rem;
}

.orange-text { color: var(--orange); }
.green-text { color: var(--green); }
.blue-text { color: var(--blue); }
.navy-text { color: var(--navy); }

.metric-label {
    color: var(--text-light);
    font-weight: 500;
}

/* Project Tech */
.project-tech {
    margin-top: 4rem;
    margin-bottom: 4rem;
}

.tech-header {
    text-align: center;
    margin-bottom: 2rem;
}

.tech-header h3 {
    font-size: 2rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
}

.tech-header p {
    color: var(--text-light);
    font-size: 1.1rem;
}

.tech-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 1.5rem;
    max-width: 800px;
    margin: 0 auto;
}

.tech-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
    padding: 1.5rem;
    background: var(--card-bg);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
}

.tech-item:hover {
    transform: translateY(-2px);
    border-color: var(--orange);
}

.tech-item i {
    width: 24px;
    height: 24px;
    color: var(--orange);
}

.tech-item span {
    font-weight: 600;
    color: #FFFFFF;
}

/* Project CTA */
.project-cta {
    background: radial-gradient(ellipse at center, rgba(170, 92, 47, 0.1) 0%, transparent 70%);
    padding: 4rem 0;
    text-align: center;
}

.cta-content h2 {
    font-size: 2.5rem;
    font-weight: 800;
    margin-bottom: 1rem;
}

.cta-content p {
    font-size: 1.25rem;
    color: var(--text-light);
    max-width: 600px;
    margin: 0 auto 3rem;
}

.cta-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-button {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 1rem 2rem;
    border-radius: 50px;
    text-decoration: none;
    font-weight: 600;
    font-size: 1.1rem;
    transition: all 0.3s ease;
}

.cta-button.primary {
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 75%, #2C6ECB 100%);
    color: #FFFFFF;
    box-shadow: 0 8px 30px rgba(170, 92, 47, 0.3);
}

.cta-button.primary:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 40px rgba(170, 92, 47, 0.4);
}

.cta-button.secondary {
    background: transparent;
    color: #FFFFFF;
    border: 2px solid rgba(255, 255, 255, 0.2);
}

.cta-button.secondary:hover {
    border-color: var(--orange);
    color: var(--orange);
}

/* Section Divider */
.section-divider-line {
    position: relative;
    width: 100%;
    height: 1px;
    margin: 3rem 0;
    background: linear-gradient(
        90deg,
        transparent 0%,
        transparent 20%,
        rgba(255, 255, 255, 0.3) 50%,
        transparent 80%,
        transparent 100%
    );
    z-index: 10;
}

.section-divider-line::before {
    content: '';
    position: absolute;
    top: -2rem;
    left: 0;
    width: 100%;
    height: calc(1px + 4rem);
    background: rgba(11, 13, 16, 0.8);
    z-index: -1;
}

/* Footer */
.project-footer {
    background: var(--card-bg);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding: 3rem 0 1.5rem;
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2rem;
}

.footer-brand {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.footer-logo {
    width: 120px;
    height: auto;
}

.footer-brand p {
    color: var(--text-light);
    max-width: 300px;
}

.footer-links {
    display: flex;
    gap: 1rem;
}

.footer-links a {
    width: 50px;
    height: 50px;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-light);
    text-decoration: none;
    transition: all 0.3s ease;
}

.footer-links a:hover {
    background: var(--orange);
    color: #FFFFFF;
    transform: translateY(-2px);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    color: var(--text-light);
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 0 1rem;
    }

    .cover-content {
        grid-template-columns: 1fr;
        gap: 2rem;
        text-align: center;
        padding: 0 1rem;
        min-height: auto;
    }

    .cover-title {
        font-size: 2.5rem;
    }

    .cover-dashboard-image {
        transform: none;
    }

    .about-project {
        margin: 2rem 0;
        padding: 3rem 0;
    }

    .about-content {
        padding: 0 1rem;
    }

    .about-section {
        padding: 1.5rem;
    }

    .carousel-controls {
        padding: 0 0.5rem;
    }

    .carousel-btn {
        width: 50px;
        height: 50px;
    }

    .carousel-btn i {
        width: 20px;
        height: 20px;
    }

    .image-caption {
        padding: 1.5rem;
    }

    .image-caption h3 {
        font-size: 1.25rem;
    }

    .image-caption p {
        font-size: 1rem;
    }

    .project-title {
        font-size: 2.5rem;
    }

    .project-description {
        font-size: 1.1rem;
    }

    .feature-grid {
        grid-template-columns: 1fr;
    }

    .metrics-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .tech-grid {
        grid-template-columns: repeat(3, 1fr);
    }

    .cta-buttons {
        flex-direction: column;
        align-items: center;
    }

    .footer-content {
        flex-direction: column;
        text-align: center;
        gap: 2rem;
    }

    .project-metrics {
        padding: 2rem 1rem;
    }
}

@media (max-width: 480px) {
    .cover-title {
        font-size: 2rem;
    }

    .cover-content {
        padding: 0 1rem;
    }

    .about-title {
        font-size: 2rem;
    }

    .project-title {
        font-size: 2rem;
    }

    .metrics-grid {
        grid-template-columns: 1fr;
    }

    .tech-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .project-header {
        padding: 0.75rem 0;
    }

    .contact-cta {
        padding: 0.5rem 1rem;
        font-size: 0.9rem;
    }
}

public/css/blog.css.content:
/* Blog Styles */
.blog {
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    color: white;
    position: relative;
    overflow: hidden;
}

.blog::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background:
        radial-gradient(circle at 20% 30%, rgba(170, 92, 47, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 70%, rgba(44, 110, 203, 0.08) 0%, transparent 50%),
        radial-gradient(circle at 40% 90%, rgba(150, 180, 168, 0.06) 0%, transparent 50%);
    pointer-events: none;
}

.blog .container {
    position: relative;
    z-index: 2;
}

.blog .section-header h2 {
    color: white;
    text-align: center;
    font-size: 3.5rem;
    margin-bottom: 2rem;
    background: none;
    -webkit-text-fill-color: white;
}

.blog .section-header p {
    color: rgba(255, 255, 255, 0.8);
    text-align: center;
    max-width: 600px;
    margin: 0 auto 3rem;
}

.blog-search-container {
    display: flex;
    flex-direction: row;
    gap: 2rem;
    margin-bottom: 3rem;
    align-items: center;
    justify-content: center;
}

@media (max-width: 768px) {
    .blog-search-container {
        flex-direction: column;
        align-items: center;
    }
}

.search-bar {
    position: relative;
    max-width: 400px;
    width: 100%;
    flex-shrink: 0;
}

.search-bar i {
    position: absolute;
    left: 1rem;
    top: 50%;
    transform: translateY(-50%);
    color: #AA5C2F;
    padding: 0;
    z-index: 2;
}

.search-bar input {
    position: relative;
    z-index: 1;
    width: 100%;
    padding: 1rem 1rem 1rem 4rem;
    background: linear-gradient(135deg, rgba(45, 45, 45, 0.9) 0%, rgba(32, 32, 32, 0.95) 100%);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: 50px;
    color: white;
    font-family: 'Montserrat', sans-serif;
    font-size: 1rem;
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
}

.search-bar input::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.search-bar input:focus {
    outline: none;
    border-color: #AA5C2F;
    box-shadow: 0 0 0 3px rgba(170, 92, 47, 0.2), 0 12px 35px rgba(0, 0, 0, 0.3);
    background: linear-gradient(135deg, rgba(45, 45, 45, 0.95) 0%, rgba(32, 32, 32, 1) 100%);
}

.blog-topics {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    justify-content: center;
    align-items: center;
}

.topic-label {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    font-weight: 600;
    margin-right: 0.5rem;
}

.topic-item {
    padding: 0.6rem 1.5rem;
    background: linear-gradient(135deg, rgba(45, 45, 45, 0.8) 0%, rgba(32, 32, 32, 0.9) 100%);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: 25px;
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.85rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    backdrop-filter: blur(10px);
    position: relative;
    overflow: hidden;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.topic-item::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.6s ease;
}

.topic-item:hover::before {
    left: 100%;
}

.topic-item:hover {
    transform: translateY(-2px);
    border-color: rgba(170, 92, 47, 0.3);
    color: white;
    box-shadow: 0 8px 25px rgba(170, 92, 47, 0.2);
}

.topic-item.active {
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    border-color: rgba(255, 255, 255, 0.2);
    color: white;
    box-shadow: 0 8px 25px rgba(170, 92, 47, 0.3), inset 0 1px 0 rgba(255, 255, 255, 0.2);
    transform: translateY(-2px);
}

.blog-grid {
    display: grid;
    gap: 2rem;
    margin-top: 3rem;
    grid-template-columns: 1fr;
}

@media (min-width: 768px) {
    .blog-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (min-width: 1024px) {
    .blog-grid {
        grid-template-columns: repeat(3, 1fr);
    }
}

.blog-card {
    background: linear-gradient(135deg, rgba(45, 45, 45, 0.9) 0%, rgba(32, 32, 32, 0.95) 50%, rgba(26, 26, 26, 0.9) 100%);
    border-radius: 20px;
    padding: 0;
    overflow: hidden;
    transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    border: 1px solid rgba(255, 255, 255, 0.08);
    position: relative;
    backdrop-filter: blur(10px);
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3), 0 8px 25px rgba(170, 92, 47, 0.05), inset 0 1px 0 rgba(255, 255, 255, 0.05);
}

.blog-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    opacity: 0;
    transition: opacity 0.3s ease;
}

.blog-card:hover::before {
    opacity: 1;
}

.blog-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 30px 80px rgba(0, 0, 0, 0.4), 0 12px 35px rgba(170, 92, 47, 0.15), inset 0 1px 0 rgba(255, 255, 255, 0.1);
    border-color: rgba(170, 92, 47, 0.2);
}

.blog-card-image {
    width: 100%;
    height: 200px;
    background: linear-gradient(135deg, rgba(170, 92, 47, 0.15) 0%, rgba(150, 180, 168, 0.12) 25%, rgba(124, 193, 223, 0.1) 50%, rgba(44, 110, 203, 0.08) 100%);
    position: relative;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
}

.blog-card-image::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, rgba(170, 92, 47, 0.1) 0%, rgba(150, 180, 168, 0.08) 25%, rgba(124, 193, 223, 0.08) 50%, rgba(44, 110, 203, 0.06) 100%);
    opacity: 0.8;
}

.blog-card-icon {
    position: relative;
    z-index: 2;
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 10px 30px rgba(170, 92, 47, 0.4);
}

.blog-card-icon i {
    color: white;
    font-size: 1.5rem;
}

.blog-card-content {
    padding: 2rem;
    padding-bottom: 5rem;
    position: relative;
}

.blog-card-badges {
    display: flex;
    gap: 0.5rem;
    margin-bottom: 1.5rem;
    flex-wrap: wrap;
}

.blog-badge {
    padding: 0.4rem 1rem;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.blog-badge.ai {
    background: linear-gradient(135deg, rgba(170, 92, 47, 0.2) 0%, rgba(170, 92, 47, 0.3) 100%);
    color: #FF9D7A;
    border: 1px solid rgba(170, 92, 47, 0.3);
}

.blog-badge.machine-learning {
    background: linear-gradient(135deg, rgba(44, 110, 203, 0.2) 0%, rgba(44, 110, 203, 0.3) 100%);
    color: #7CC1DF;
    border: 1px solid rgba(44, 110, 203, 0.3);
}

.blog-badge.automacao {
    background: linear-gradient(135deg, rgba(150, 180, 168, 0.2) 0%, rgba(150, 180, 168, 0.3) 100%);
    color: #96B4A8;
    border: 1px solid rgba(150, 180, 168, 0.3);
}

.blog-badge.dashboards {
    background: linear-gradient(135deg, rgba(124, 193, 223, 0.2) 0%, rgba(124, 193, 223, 0.3) 100%);
    color: #7CC1DF;
    border: 1px solid rgba(124, 193, 223, 0.3);
}

.blog-badge.cases {
    background: linear-gradient(135deg, rgba(183, 183, 183, 0.2) 0%, rgba(183, 183, 183, 0.3) 100%);
    color: #B7B7B7;
    border: 1px solid rgba(183, 183, 183, 0.3);
}

.blog-badge.bi {
    background: linear-gradient(135deg, rgba(150, 180, 168, 0.2) 0%, rgba(150, 180, 168, 0.3) 100%);
    color: #96B4A8;
    border: 1px solid rgba(150, 180, 168, 0.3);
}

.blog-card h3 {
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-size: 1.3rem;
    font-weight: 700;
    line-height: 1.4;
    margin-bottom: 1rem;
    text-shadow: none;
}

.blog-card p {
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.6;
    margin-bottom: 1.5rem;
    font-size: 0.95rem;
}

.blog-meta {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
    font-size: 0.85rem;
    color: rgba(255, 255, 255, 0.6);
}

.blog-meta i {
    width: 16px;
    height: 16px;
    color: #AA5C2F;
}

.blog-link {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    color: white;
    text-decoration: none;
    font-weight: 600;
    padding: 0.8rem 1.5rem;
    border-radius: 25px;
    transition: all 0.3s ease;
    font-size: 0.9rem;
    box-shadow: 0 8px 25px rgba(170, 92, 47, 0.3);
    position: absolute;
    width: 50%;
    overflow: hidden;
    left: 50%;
    /* Começa do meio */
    transform: translateX(-50%);
    /* Centraliza horizontalmente */
    bottom: 1rem;
}

public/css/sobre.css.content:
/* Sobre Page Specific Styles */

/* Hero About Section */
.hero-about {
    min-height: 100vh;
    display: flex;
    align-items: center;
    position: relative;
    background: linear-gradient(
        180deg,
        hsl(220, 40%, 2%) 0%,
        hsla(220, 35%, 1.5%, 0.8) 30%,
        hsl(220, 30%, 1%) 70%,
        hsl(220, 25%, 0.5%) 100%
    );
    padding: 8rem 0 4rem;
}

.breadcrumb {
    margin-bottom: 2rem;
    font-family: 'Montserrat', sans-serif;
}

.breadcrumb-item {
    color: #CFCFCD;
    font-size: 0.875rem;
    font-weight: 400;
    opacity: 0.8;
}


.hero-title {
    font-family: 'Montserrat', sans-serif;
    font-size: 3.5rem;
    font-weight: 800;
    color: #FFFFFF;
    line-height: 1.2;
    margin-bottom: 3rem;
    text-align: left;
}

.gradient-text {
    background: linear-gradient(135deg, #AA5C2F 0%, #96B4A8 25%, #7CC1DF 50%, #2C6ECB 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.hero-text {
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.text-section {
    display: flex;
    gap: 1.5rem;
    align-items: flex-start;
}

.section-icon {
    flex-shrink: 0;
}

.icon-wrapper {
    width: 60px;
    height: 60px;
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 
        0 8px 32px rgba(0, 0, 0, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.1),
        0 1px 0 rgba(0, 0, 0, 0.2);
}

.icon-wrapper.blue-gradient {
    background: linear-gradient(145deg, #2C6ECB, #7CC1DF);
}

.icon-wrapper.green-gradient {
    background: linear-gradient(145deg, #96B4A8, #7CC1DF);
}

.icon-wrapper.orange-gradient {
    background: linear-gradient(145deg, #AA5C2F, #96B4A8);
}

.text-content {
    flex: 1;
}

.hero-description {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.125rem;
    font-weight: 400;
    color: #CFCFCD;
    line-height: 1.7;
    margin: 0;
}

.hero-description-enhanced {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.25rem;
    font-weight: 400;
    color: #FFFFFF;
    line-height: 1.8;
    margin-bottom: 1.5rem;
    opacity: 0.95;
}

.about-description {
    max-width: 100%;
}

.hero-visual {
    display: flex;
    justify-content: center;
    align-items: center;
}

.visual-container {
    position: relative;
    width: 100%;
    max-width: 500px;
}

.liquid-glass-card {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 24px;
    padding: 3rem 2rem;
    box-shadow: 
        0 20px 60px rgba(0, 0, 0, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.1),
        0 1px 0 rgba(0, 0, 0, 0.2);
    position: relative;
    overflow: hidden;
}

.liquid-glass-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        135deg,
        rgba(170, 92, 47, 0.1) 0%,
        rgba(150, 180, 168, 0.1) 25%,
        rgba(124, 193, 223, 0.1) 50%,
        rgba(44, 110, 203, 0.1) 100%
    );
    border-radius: 24px;
    z-index: -1;
}

.chart-container {
    height: 200px;
    display: flex;
    align-items: end;
    justify-content: center;
    margin-bottom: 2rem;
}

.chart-bars {
    display: flex;
    gap: 1rem;
    align-items: end;
    height: 100%;
}

.bar {
    width: 24px;
    border-radius: 12px;
    transition: height 0.3s ease;
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
}

.floating-elements {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
}

.floating-card {
    position: absolute;
    width: 48px;
    height: 48px;
    border-radius: 12px;
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    display: flex;
    align-items: center;
    justify-content: center;
    animation: float 3s ease-in-out infinite;
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

.floating-card.orange-glow {
    background: rgba(170, 92, 47, 0.2);
    top: 20%;
    right: 10%;
    animation-delay: 0s;
}

.floating-card.blue-glow {
    background: rgba(44, 110, 203, 0.2);
    top: 60%;
    left: 5%;
    animation-delay: 1s;
}

.floating-card.green-glow {
    background: rgba(150, 180, 168, 0.2);
    bottom: 25%;
    right: 20%;
    animation-delay: 2s;
}

@keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
}

/* Mission Section */
.mission-section,
.team-section,
.technology-section {
    padding: 5rem 0;
    position: relative;
}

.mission-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-top: 3rem;
}

.mission-card {
    text-align: center;
    padding: 2.5rem 2rem;
}

.neumorphic-card {
    background: rgba(255, 255, 255, 0.03);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.08);
    border-radius: 20px;
    box-shadow: 
        0 16px 40px rgba(0, 0, 0, 0.3),
        inset 0 1px 0 rgba(255, 255, 255, 0.08),
        0 1px 0 rgba(0, 0, 0, 0.1);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.neumorphic-card:hover {
    transform: translateY(-4px);
    box-shadow: 
        0 24px 60px rgba(0, 0, 0, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.12),
        0 1px 0 rgba(0, 0, 0, 0.1);
}

.mission-icon {
    width: 80px;
    height: 80px;
    border-radius: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1.5rem;
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
}

.mission-title {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.5rem;
    font-weight: 700;
    color: #FFFFFF;
    margin-bottom: 1rem;
}

.mission-description {
    font-family: 'Montserrat', sans-serif;
    font-size: 1rem;
    font-weight: 400;
    color: #CFCFCD;
    line-height: 1.6;
    margin: 0;
}

/* Team Grid */
.team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 2rem;
    margin-top: 3rem;
    margin-bottom: 3rem;
}

.team-member {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    padding: 2rem;
    text-align: center;
}

.member-avatar {
    flex-shrink: 0;
}

.avatar-circle {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
    color: white;
}

.avatar-circle.ceo-gradient {
    background: linear-gradient(145deg, #AA5C2F, #96B4A8);
}

.avatar-circle.backend-gradient {
    background: linear-gradient(145deg, #2C6ECB, #7CC1DF);
}

.avatar-circle.frontend-gradient {
    background: linear-gradient(145deg, #96B4A8, #7CC1DF);
}

.avatar-circle.analyst-gradient {
    background: linear-gradient(145deg, #7CC1DF, #2C6ECB);
}

.avatar-circle.designer-gradient {
    background: linear-gradient(145deg, #AA5C2F, #7CC1DF);
}

.member-info {
    flex: 1;
}

.member-name {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.25rem;
    font-weight: 700;
    color: #FFFFFF;
    margin-bottom: 0.5rem;
    letter-spacing: 0.5px;
}

.member-role {
    font-family: 'Montserrat', sans-serif;
    font-size: 0.875rem;
    font-weight: 600;
    color: #7CC1DF;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: 1rem;
    display: block;
}

.member-description {
    font-family: 'Montserrat', sans-serif;
    font-size: 0.95rem;
    font-weight: 400;
    color: #CFCFCD;
    line-height: 1.6;
    margin: 0;
}

.team-bottom-message {
    margin-top: 3rem;
    padding: 2.5rem;
    display: flex;
    align-items: center;
    gap: 2rem;
    text-align: left;
}

.message-icon {
    flex-shrink: 0;
}

.icon-x {
    width: 70px;
    height: 70px;
    border-radius: 12px;
    background: transparent;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    border: 2px solid white;
}

.message-content {
    flex: 1;
}

.message-content p {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.125rem;
    font-weight: 400;
    color: #FFFFFF;
    line-height: 1.7;
    margin: 0;
}

/* Technology Section - Rebuild */
.technology-section {
    padding: 4rem 0;
}

.tech-cards-container {
    display: flex;
    flex-wrap: wrap;
    gap: 2rem;
    margin-top: 3rem;
    justify-content: center;
}

.tech-card {
    flex: 0 1 280px;
    min-height: 200px;
    padding: 2rem;
    background: transparent;
    border: 1px solid rgba(255, 255, 255, 0.08);
    border-radius: 20px;
    box-shadow: 
        0 8px 32px rgba(0, 0, 0, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.08);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.tech-card:hover {
    transform: translateY(-4px);
    box-shadow: 
        0 24px 60px rgba(0, 0, 0, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.12);
}

.tech-card-header {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-bottom: 1.5rem;
}

.tech-icon {
    width: 24px;
    height: 24px;
}

.tech-icon-orange {
    color: #AA5C2F;
}

.tech-icon-green {
    color: #96B4A8;
}

.tech-icon-blue-light {
    color: #7CC1DF;
}

.tech-icon-blue-dark {
    color: #2C6ECB;
}

.tech-card-title {
    font-family: 'Montserrat', sans-serif;
    font-size: 1.125rem;
    font-weight: 600;
    color: #FFFFFF;
    margin: 0;
}

.tech-tags-wrapper {
    display: flex;
    flex-wrap: wrap;
    gap: 0.625rem;
}

.tech-tag {
    background: transparent;
    backdrop-filter: none;
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 20px;
    padding: 0.5rem 1rem;
    font-family: 'Montserrat', sans-serif;
    font-size: 0.875rem;
    font-weight: 500;
    color: #FFFFFF;
    transition: all 0.3s ease;
    white-space: nowrap;
}

.tech-tag:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.3);
    transform: translateY(-2px);
}

/* Responsive Design */
@media (max-width: 1024px) {
    .hero-content {
        grid-template-columns: 1fr;
        gap: 3rem;
    }

    .hero-title {
        font-size: 2.5rem;
        text-align: center;
    }

    .text-section {
        text-align: center;
        flex-direction: column;
        align-items: center;
        gap: 1rem;
    }
}

@media (max-width: 768px) {
    .hero-about {
        padding: 6rem 0 3rem;
    }

    .hero-title {
        font-size: 2rem;
        margin-bottom: 2rem;
    }


    .hero-description,
    .hero-description-enhanced {
        font-size: 1rem;
    }

    .team-grid {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }

    .tech-cards-container {
        flex-direction: column;
        gap: 1.5rem;
    }

    .tech-card {
        flex: 1 1 100%;
        min-width: 100%;
    }

    .team-member {
        padding: 1.5rem;
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .team-bottom-message {
        flex-direction: column;
        text-align: center;
        gap: 1.5rem;
    }

    .liquid-glass-card {
        padding: 2rem 1.5rem;
    }

    .chart-container {
        height: 150px;
    }
}

@media (max-width: 480px) {
    .hero-title {
        font-size: 1.75rem;
    }


    .hero-description {
        font-size: 0.95rem;
    }

    .text-section {
        gap: 0.75rem;
    }

    .icon-wrapper {
        width: 50px;
        height: 50px;
    }

    .floating-card {
        width: 40px;
        height: 40px;
    }
}
